import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [error, setError] = useState(null);
  const [editingStudent, setEditingStudent] = useState(null);
  const [updatedStudent, setUpdatedStudent] = useState({
    rollNumber: '',
    firstName: '',
    lastName: '',
    email: '',
    age: '',
    course: ''
  });

  // Function to fetch students
  const fetchStudents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/studentsview');
      console.log('Students fetched:', response.data); // Log the response
      setStudents(response.data.data); // Set the students' data
    } catch (err) {
      console.error('Error fetching students:', err);  // Log the entire error
      setError('Failed to fetch students. Please try again.');
    }
  };

  // Fetch the students when the component mounts
  useEffect(() => {
    fetchStudents();
  }, []);

  // Handle student data change for updating
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedStudent((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handle form submission for updating student
  const handleUpdateStudent = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.put(
        `http://localhost:5000/api/students/${editingStudent.id}`,
        updatedStudent
      );
      console.log(response.data); // Log success message
      alert('Student updated successfully');
      setEditingStudent(null); // Close the update form
      setUpdatedStudent({
        rollNumber: '',
        firstName: '',
        lastName: '',
        email: '',
        age: '',
        course: ''
      });
      // Refresh the students list after update
      fetchStudents(); // Call the function to refresh the list
    } catch (err) {
      console.error('Error updating student:', err);
      alert('Failed to update student. Please try again.');
    }
  };

  // Edit student details
  const handleEdit = (student) => {
    setEditingStudent(student);
    setUpdatedStudent({
      rollNumber: student.rollNumber,
      firstName: student.firstName,
      lastName: student.lastName,
      email: student.email,
      age: student.age,
      course: student.course,
    });
  };

  const handleDeleteStudent = async (studentId) => {
    if (window.confirm('Are you sure you want to delete this student?')) {
      try {
        const response = await axios.delete(`http://localhost:5000/api/studentsdelete/${studentId}`);

        console.log('Delete response:', response.data); // Log the successful response
        alert('Student deleted successfully');
        // Refresh the students list after deletion
        fetchStudents(); // Call the function to refresh the list
      } catch (err) {
        console.error('Error deleting student:', err.response ? err.response.data : err);
        alert('Failed to delete student. Please try again.');
      }
    }
  };

  // Inline styles for buttons and other elements
  const styles = {
    button: {
      padding: '8px 15px',
      margin: '5px',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      transition: 'background-color 0.3s',
    },
    buttonBlue: {
      backgroundColor: '#0066cc',  // Blue button
      color: 'white',
    },
    buttonBlueHover: {
      backgroundColor: '#004d99',  // Darker blue button on hover
    },
    buttonRed: {
      backgroundColor: '#ff6f61',  // Red button for delete
      color: 'white',
    },
    buttonRedHover: {
      backgroundColor: '#ff4b3a',  // Darker red button on hover
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
      border: '1px solid #ccc',
      padding: '20px',
      maxWidth: '500px',
      margin: '20px auto',
      backgroundColor: '#f9f9f9',
      borderRadius: '8px',
    },
    input: {
      padding: '8px',
      fontSize: '1rem',
      border: '1px solid #ccc',
      borderRadius: '4px',
      marginBottom: '15px',
    },
    container: {
      padding: '20px',
      maxWidth: '1200px',
      margin: '0 auto',
    },
    table: {
      width: '100%',
      borderCollapse: 'collapse',
    },
    thTd: {
      padding: '10px',
      textAlign: 'center',
      border: '1px solid #ddd',
    },
    tableRow: {
      transition: 'background-color 0.3s',
    },
    tableRowHover: {
      backgroundColor: '#f1f1f1', // Light gray background on hover
    },
  };

  return (
    <div className="student-list" style={styles.container}>
      <h2>Student List</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {editingStudent ? (
        <div className="edit-form" style={styles.form}>
          <h3>Edit Student</h3>
          <form onSubmit={handleUpdateStudent}>
            <div>
              <label>Roll Number:</label>
              <input
                type="text"
                name="rollNumber"
                value={updatedStudent.rollNumber}
                onChange={handleInputChange}
                style={styles.input}
                required
              />
            </div>
            <div>
              <label>First Name:</label>
              <input
                type="text"
                name="firstName"
                value={updatedStudent.firstName}
                onChange={handleInputChange}
                style={styles.input}
                required
              />
            </div>
            <div>
              <label>Last Name:</label>
              <input
                type="text"
                name="lastName"
                value={updatedStudent.lastName}
                onChange={handleInputChange}
                style={styles.input}
                required
              />
            </div>
            <div>
              <label>Email:</label>
              <input
                type="email"
                name="email"
                value={updatedStudent.email}
                onChange={handleInputChange}
                style={styles.input}
                required
              />
            </div>
            <div>
              <label>Age:</label>
              <input
                type="number"
                name="age"
                value={updatedStudent.age}
                onChange={handleInputChange}
                style={styles.input}
                required
              />
            </div>
            <div>
              <label>Course:</label>
              <input
                type="text"
                name="course"
                value={updatedStudent.course}
                onChange={handleInputChange}
                style={styles.input}
                required
              />
            </div>
            <button type="submit" style={{ ...styles.button, ...styles.buttonBlue }}>Update Student</button>
          </form>
          <button onClick={() => setEditingStudent(null)} style={{ ...styles.button, ...styles.buttonRed }}>Cancel</button>
        </div>
      ) : (
        <table style={styles.table} align="center">
          <thead>
            <tr>
              <th style={styles.thTd}>Roll Number</th>
              <th style={styles.thTd}>First Name</th>
              <th style={styles.thTd}>Last Name</th>
              <th style={styles.thTd}>Email</th>
              <th style={styles.thTd}>Age</th>
              <th style={styles.thTd}>Course</th>
              <th style={styles.thTd}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {students.length > 0 ? (
              students.map((student) => (
                <tr 
                  key={student.id} 
                  style={styles.tableRow} 
                  onMouseEnter={(e) => e.target.style.backgroundColor = styles.tableRowHover.backgroundColor} 
                  onMouseLeave={(e) => e.target.style.backgroundColor = ''}
                >
                  <td style={styles.thTd}>{student.rollNumber}</td>
                  <td style={styles.thTd}>{student.firstName}</td>
                  <td style={styles.thTd}>{student.lastName}</td>
                  <td style={styles.thTd}>{student.email}</td>
                  <td style={styles.thTd}>{student.age}</td>
                  <td style={styles.thTd}>{student.course}</td>
                  <td style={styles.thTd}>
                    <button
                      onClick={() => handleEdit(student)}
                      style={{ ...styles.button, ...styles.buttonBlue }}
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDeleteStudent(student.id)}
                      style={{ ...styles.button, ...styles.buttonRed }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7" style={styles.thTd}>No students available</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default StudentList;
