import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [error, setError] = useState(null);
  const [editingStudent, setEditingStudent] = useState(null);
  const [updatedStudent, setUpdatedStudent] = useState({
    rollNumber: '',
    firstName: '',
    lastName: '',
    email: '',
    age: '',
    course: '',
  });

  const fetchStudents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/studentsview');
      setStudents(response.data.data);
    } catch (err) {
      setError('Failed to fetch students. Please try again.');
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedStudent((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleUpdateStudent = async (e) => {
    e.preventDefault();

    try {
      await axios.put(
        `http://localhost:5000/api/students/${editingStudent.id}`,
        updatedStudent
      );
      alert('Student updated successfully');
      setEditingStudent(null);
      setUpdatedStudent({
        rollNumber: '',
        firstName: '',
        lastName: '',
        email: '',
        age: '',
        course: '',
      });
      fetchStudents();
    } catch (err) {
      alert('Failed to update student. Please try again.');
    }
  };

  const handleEdit = (student) => {
    setEditingStudent(student);
    setUpdatedStudent({
      rollNumber: student.rollNumber,
      firstName: student.firstName,
      lastName: student.lastName,
      email: student.email,
      age: student.age,
      course: student.course,
    });
  };

  const handleDeleteStudent = async (studentId) => {
    if (window.confirm('Are you sure you want to delete this student?')) {
      try {
        await axios.delete(`http://localhost:5000/api/studentsdelete/${studentId}`);
        alert('Student deleted successfully');
        fetchStudents();
      } catch (err) {
        alert('Failed to delete student. Please try again.');
      }
    }
  };

  const styles = {
    navbar: {
      display: 'flex',
      justifyContent: 'space-between',
      padding: '10px 20px',
      backgroundColor: '#333',
      color: '#fff',
      fontSize: '1.2rem',
      alignItems: 'center',
    },
    navLink: {
      color: '#fff',
      textDecoration: 'none',
      margin: '0 10px',
      padding: '5px 10px',
      borderRadius: '4px',
      transition: 'background-color 0.3s',
    },
    navLinkHover: {
      backgroundColor: '#007acc',
    },
    container: {
      padding: '30px 20px',
      maxWidth: '1200px',
      margin: '0 auto',
      backgroundColor: '#f4f4f4',
      borderRadius: '8px',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    },
    header: {
      fontSize: '2rem',
      fontWeight: 'bold',
      color: '#333',
      textAlign: 'center',
      marginBottom: '30px',
    },
    button: {
      padding: '10px 15px',
      fontSize: '1rem',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
      margin: '5px',
      color: '#fff',
    },
    editButton: {
      backgroundColor: '#007BFF',
    },
    deleteButton: {
      backgroundColor: '#FF4136',
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
      padding: '20px',
      backgroundColor: '#fff',
      borderRadius: '8px',
      maxWidth: '600px',
      margin: '0 auto',
    },
    input: {
      padding: '10px',
      fontSize: '1rem',
      border: '1px solid #ccc',
      borderRadius: '4px',
      marginBottom: '15px',
    },
  };

  return (
    <div>
      {/* Navbar */}
      <nav style={styles.navbar}>
        <div>Student Management</div>
        <div>
          <Link to="/" style={styles.navLink}>Home</Link>
          <Link to="/add-student" style={styles.navLink}>Add Student</Link>
          <Link to="/about" style={styles.navLink}>About</Link>
        </div>
      </nav>

      {/* Student List */}
      <div style={styles.container}>
        <h2 style={styles.header}>Student List</h2>
        {editingStudent ? (
          <form style={styles.form} onSubmit={handleUpdateStudent}>
            <input
              type="text"
              name="rollNumber"
              value={updatedStudent.rollNumber}
              onChange={handleInputChange}
              placeholder="Roll Number"
              style={styles.input}
              required
            />
            <input
              type="text"
              name="firstName"
              value={updatedStudent.firstName}
              onChange={handleInputChange}
              placeholder="First Name"
              style={styles.input}
              required
            />
            <input
              type="text"
              name="lastName"
              value={updatedStudent.lastName}
              onChange={handleInputChange}
              placeholder="Last Name"
              style={styles.input}
              required
            />
            <input
              type="email"
              name="email"
              value={updatedStudent.email}
              onChange={handleInputChange}
              placeholder="Email"
              style={styles.input}
              required
            />
            <input
              type="number"
              name="age"
              value={updatedStudent.age}
              onChange={handleInputChange}
              placeholder="Age"
              style={styles.input}
              required
            />
            <input
              type="text"
              name="course"
              value={updatedStudent.course}
              onChange={handleInputChange}
              placeholder="Course"
              style={styles.input}
              required
            />
            <button
              type="submit"
              style={{ ...styles.button, ...styles.editButton }}
            >
              Update
            </button>
            <button
              onClick={() => setEditingStudent(null)}
              style={{ ...styles.button, ...styles.deleteButton }}
            >
              Cancel
            </button>
          </form>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Roll Number</th>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Email</th>
                <th>Age</th>
                <th>Course</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {students.map((student) => (
                <tr key={student.id}>
                  <td>{student.rollNumber}</td>
                  <td>{student.firstName}</td>
                  <td>{student.lastName}</td>
                  <td>{student.email}</td>
                  <td>{student.age}</td>
                  <td>{student.course}</td>
                  <td>
                    <button
                      onClick={() => handleEdit(student)}
                      style={{ ...styles.button, ...styles.editButton }}
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDeleteStudent(student.id)}
                      style={{ ...styles.button, ...styles.deleteButton }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default StudentList;
