import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:5000/api/login",
        { email, password },
        { withCredentials: true }
      );
      if (response.data.success) {
        localStorage.setItem("role", response.data.role);
        localStorage.setItem("user", JSON.stringify(response.data.student || {}));
        if (response.data.role === "admin") {
          navigate("/admin_home");
        } else if (response.data.role === "student") {
          navigate("/StudentHome");
        }
      } else {
        setError(response.data.message);
      }
    } catch (error) {
      setError("An error occurred. Please try again later.");
    }
  };

  return (
    <div style={{
      background: "linear-gradient(to right, #6a11cb, #2575fc)",
      height: "100vh",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      fontFamily: "'Poppins', sans-serif",
    }}>
      <div style={{
        background: "#fff",
        borderRadius: "15px",
        padding: "30px",
        boxShadow: "0 10px 25px rgba(0, 0, 0, 0.2)",
        maxWidth: "450px",
        width: "100%",
      }}>
        <h2 style={{ textAlign: "center", marginBottom: "20px", color: "#333", fontWeight: "600" }}>Login</h2>
        <p style={{ textAlign: "center", color: "#666", marginBottom: "30px" }}>Log in to your account</p>
        <form onSubmit={handleLogin}>
          <div style={{ marginBottom: "20px" }}>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Email"
              required
              style={{
                width: "100%",
                padding: "12px 15px",
                border: "1px solid #ddd",
                borderRadius: "8px",
                fontSize: "16px",
                outline: "none",
                transition: "box-shadow 0.3s ease",
              }}
              onFocus={(e) => (e.target.style.boxShadow = "0 0 8px rgba(0, 123, 255, 0.5)")}
              onBlur={(e) => (e.target.style.boxShadow = "none")}
            />
          </div>
          <div style={{ marginBottom: "20px", position: "relative" }}>
            <input
              type={showPassword ? "text" : "password"}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Password"
              required
              style={{
                width: "100%",
                padding: "12px 15px",
                border: "1px solid #ddd",
                borderRadius: "8px",
                fontSize: "16px",
                outline: "none",
                transition: "box-shadow 0.3s ease",
              }}
              onFocus={(e) => (e.target.style.boxShadow = "0 0 8px rgba(0, 123, 255, 0.5)")}
              onBlur={(e) => (e.target.style.boxShadow = "none")}
            />
            <span
              onClick={() => setShowPassword(!showPassword)}
              style={{
                position: "absolute",
                right: "15px",
                top: "50%",
                transform: "translateY(-50%)",
                cursor: "pointer",
                color: "#666",
              }}
              title={showPassword ? "Hide Password" : "Show Password"}
            >
              {showPassword ? "üôà" : "üëÅÔ∏è"}
            </span>
          </div>
          <div style={{ textAlign: "right", marginBottom: "20px" }}>
            <a href="/forgot-password" style={{
              color: "#2575fc",
              textDecoration: "none",
              fontSize: "14px",
            }}>
              Forgot Password?
            </a>
          </div>
          {error && (
            <p style={{ color: "red", fontSize: "14px", marginBottom: "15px", textAlign: "center" }}>
              {error}
            </p>
          )}
          <button
            type="submit"
            style={{
              width: "100%",
              padding: "12px",
              background: "linear-gradient(to right, #6a11cb, #2575fc)",
              color: "#fff",
              fontSize: "16px",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
              fontWeight: "600",
              transition: "background 0.3s ease",
            }}
            onMouseEnter={(e) => (e.target.style.background = "linear-gradient(to right, #5a00b6, #1d62db)")}
            onMouseLeave={(e) => (e.target.style.background = "linear-gradient(to right, #6a11cb, #2575fc)")}
          >
            Log In
          </button>
        </form>
        <div style={{
          display: "flex",
          justifyContent: "center",
          marginTop: "30px",
        }}>
          <button style={{
            background: "#4285F4",
            color: "#fff",
            border: "none",
            borderRadius: "50%",
            height: "45px",
            width: "45px",
            margin: "0 10px",
            cursor: "pointer",
          }}
            title="Login with Google">
            G
          </button>
          <button style={{
            background: "#3b5998",
            color: "#fff",
            border: "none",
            borderRadius: "50%",
            height: "45px",
            width: "45px",
            margin: "0 10px",
            cursor: "pointer",
          }}
            title="Login with Facebook">
            f
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
