import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const AddMarks = () => {
  const styles = {
    container: {
      display: "flex",
      minHeight: "100vh",
      fontFamily: "'Poppins', sans-serif",
      margin: 0,
      backgroundColor: "#1e1e2f",
      color: "#fff",
    },
    sidebar: {
      width: "280px",
      backgroundColor: "#25273d",
      padding: "20px",
      boxShadow: "2px 0 5px rgba(0, 0, 0, 0.5)",
    },
    sidebarTitle: {
      fontSize: "26px",
      fontWeight: "bold",
      textAlign: "center",
      marginBottom: "30px",
      color: "#f39c12",
    },
    sidebarMenu: {
      listStyleType: "none",
      padding: 0,
    },
    sidebarLink: {
      color: "#d1d1e9",
      textDecoration: "none",
      display: "block",
      padding: "12px 20px",
      borderRadius: "8px",
      margin: "8px 0",
      transition: "background 0.3s ease, color 0.3s ease",
    },
    sidebarLinkHover: {
      backgroundColor: "#f39c12",
      color: "#1e1e2f",
    },
    mainContent: {
      flexGrow: 1,
      padding: "30px",
    },
    header: {
      backgroundColor: "#3a3a52",
      color: "#f39c12",
      padding: "20px",
      borderRadius: "10px",
      textAlign: "center",
      marginBottom: "30px",
    },
    formContainer: {
      backgroundColor: "#2c2c40",
      padding: "20px",
      borderRadius: "15px",
      boxShadow: "0 8px 15px rgba(0, 0, 0, 0.3)",
      maxWidth: "600px",
      margin: "0 auto",
    },
    formField: {
      marginBottom: "20px",
    },
    label: {
      display: "block",
      marginBottom: "8px",
      fontWeight: "bold",
    },
    select: {
      width: "100%",
      padding: "10px",
      borderRadius: "5px",
      border: "1px solid #ccc",
      backgroundColor: "#1e1e2f",
      color: "#fff", // Ensure the selected text is white
      option: {
        color: "#fff", // Ensure the dropdown options are white
      },
    },

    
    input: {
      width: "100%",
      padding: "10px",
      borderRadius: "5px",
      border: "1px solid #ccc",
      backgroundColor: "#1e1e2f",
      color: "w",
    },
    submitButton: {
      backgroundColor: "#f39c12",
      color: "#1e1e2f",
      fontWeight: "bold",
      padding: "10px 20px",
      borderRadius: "8px",
      border: "none",
      cursor: "pointer",
      transition: "background-color 0.3s ease",
    },
    error: {
      color: "#e74c3c",
      marginBottom: "20px",
      fontWeight: "bold",
    },
  };

  const [students, setStudents] = useState([]);
  const [semesters] = useState(["Semester 1", "Semester 2", "Semester 3", "Semester 4"]);
  const [subjects] = useState({
    "Semester 1": ["Mathematics", "Physics", "Chemistry"],
    "Semester 2": ["Biology", "Computer Science", "History"],
    "Semester 3": ["Economics", "English Literature", "Sociology"],
    "Semester 4": ["Geography", "Political Science", "Statistics"],
  });
  const [selectedStudent, setSelectedStudent] = useState("");
  const [selectedSemester, setSelectedSemester] = useState("");
  const [marks, setMarks] = useState({});
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/students");
        const data = await response.json();
        setStudents(data);
      } catch (err) {
        setError("Failed to fetch students.");
      }
    };
    fetchStudents();
  }, []);

  const handleStudentChange = (e) => setSelectedStudent(e.target.value);
  const handleSemesterChange = (e) => {
    setSelectedSemester(e.target.value);
    setMarks({});
  };
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMarks((prevMarks) => ({ ...prevMarks, [name]: value }));
  };
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedStudent || !selectedSemester) {
      setError("Please select both a student and a semester.");
      return;
    }

    const formattedMarks = subjects[selectedSemester].reduce((acc, subject, index) => {
      acc[subject] = marks[`marks${index + 1}`];
      return acc;
    }, {});

    const payload = { semester: selectedSemester, marks: formattedMarks };

    try {
      const response = await fetch(`http://localhost:5000/api/marks/${selectedStudent}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const data = await response.json();
      if (response.ok) alert(data.message);
      else setError(data.error || "Failed to submit marks.");
    } catch {
      setError("Failed to submit marks.");
    }
  };

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h2 style={styles.sidebarTitle}>Admin Panel</h2>
        <ul style={styles.sidebarMenu}>
          <li>
            <Link to="/" style={styles.sidebarLink}>
              Dashboard
            </Link>
          </li>
          <li>
            <Link to="/StudentDetails" style={styles.sidebarLink}>
              Student Details
            </Link>
          </li>
          <li>
            <Link to="/AddMark" style={styles.sidebarLink}>
              Add Mark
            </Link>
          </li>
          <li>
            <Link to="/LogOut" style={styles.sidebarLink}>
              Logout
            </Link>
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div style={styles.mainContent}>
        <header style={styles.header}>
          <h1>Add Marks</h1>
        </header>
        <form style={styles.formContainer} onSubmit={handleSubmit}>
          {error && <div style={styles.error}>{error}</div>}
          <div style={styles.formField}>
            <label style={styles.label}>Student:</label>
            <select style={styles.select} value={selectedStudent} onChange={handleStudentChange}>
              <option value="">Select a student</option>
              {students.map((student) => (
                <option key={student.id} value={student.id}>
                  {student.name}
                </option>
              ))}
            </select>
          </div>
          <div style={styles.formField}>
            <label style={styles.label}>Semester:</label>
            <select style={styles.select} value={selectedSemester} onChange={handleSemesterChange}>
              <option value="">Select a semester</option>
              {semesters.map((semester) => (
                <option key={semester} value={semester}>
                  {semester}
                </option>
              ))}
            </select>
          </div>
          {selectedSemester && (
            <div style={styles.formField}>
              {subjects[selectedSemester].map((subject, index) => (
                <div key={subject}>
                  <label style={styles.label}>{subject}:</label>
                  <input
                    type="number"
                    style={styles.input}
                    name={`marks${index + 1}`}
                    value={marks[`marks${index + 1}`] || ""}
                    onChange={handleInputChange}
                  />
                </div>
              ))}
            </div>
          )}
          <button type="submit" style={styles.submitButton}>
            Submit Marks
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddMarks;
