import React from 'react';
import { Link } from 'react-router-dom';

const StudentHomePage = () => {
  // Inline Styles
  const containerStyle = {
    fontFamily: 'Arial, sans-serif',
    padding: '30px',
    backgroundColor: '#f7f7f7',
    minHeight: '100vh',
  };

  const headerStyle = {
    textAlign: 'center',
    color: '#4CAF50',
    fontSize: '36px',
    fontWeight: 'bold',
    marginBottom: '20px',
  };

  const navBarStyle = {
    display: 'flex',
    justifyContent: 'center',
    backgroundColor: '#007BFF',
    padding: '15px 0',
    borderRadius: '50px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    marginBottom: '30px',
  };

  const linkStyle = {
    textDecoration: 'none',
    color: 'white',
    fontWeight: 'bold',
    margin: '0 20px',
    fontSize: '18px',
    transition: 'transform 0.3s ease, color 0.3s ease',
  };

  const linkHoverStyle = {
    transform: 'scale(1.1)',
    color: '#FFD700',
  };

  const sectionStyle = {
    textAlign: 'center',
    marginBottom: '40px',
  };

  const footerStyle = {
    textAlign: 'center',
    color: '#777',
    fontSize: '14px',
    borderTop: '1px solid #ddd',
    paddingTop: '20px',
    marginTop: '40px',
  };

  return (
    <div style={containerStyle}>
      <header style={headerStyle}>
        <h1>Welcome, Student!</h1>
      </header>

      <div style={navBarStyle}>
        <Link to="/profile" style={linkStyle} className="nav-link">Profile</Link>
        <Link to="/courses" style={linkStyle} className="nav-link">Courses</Link>
        <Link to="/assignments" style={linkStyle} className="nav-link">Assignments</Link>
        <Link to="/grades" style={linkStyle} className="nav-link">Grades</Link>
        <Link to="/logout" style={linkStyle} className="nav-link">Logout</Link>
      </div>

      <section style={sectionStyle}>
        <h2>Quick Links</h2>
        <p>Use the links above to navigate to various sections of your student portal.</p>
      </section>

      <footer style={footerStyle}>
        <p>&copy; 2024 Student Portal</p>
      </footer>
    </div>
  );
};

export default StudentHomePage;
