import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StudentDetails = () => {
  const [student, setStudent] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchStudentDetails = async () => {
      try {
        const email = JSON.parse(localStorage.getItem('user')).email; // Get email from session storage
        const response = await axios.post('http://localhost:5000/api/student-details', { email });
        
        if (response.data.success) {
          setStudent(response.data.student);
        } else {
          setError(response.data.message);
        }
      } catch (error) {
        console.error('Error fetching student details:', error);
        setError('An error occurred while fetching student details.');
      }
    };

    fetchStudentDetails();
  }, []);

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  if (!student) {
    return <p>Loading student details...</p>;
  }

  return (
    <div>
      <h2>Student Details</h2>
      <table border="1" cellPadding="10">
        <tbody>
          <tr>
            <td>First Name</td>
            <td>{student.firstName}</td>
          </tr>
          <tr>
            <td>Last Name</td>
            <td>{student.lastName}</td>
          </tr>
          <tr>
            <td>Email</td>
            <td>{student.email}</td>
          </tr>
          <tr>
            <td>Age</td>
            <td>{student.age}</td>
          </tr>
          <tr>
            <td>Course</td>
            <td>{student.course}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default StudentDetails;
