import axios from 'axios';
import { useEffect, useState } from 'react';

const StudentDetails = () => {
  const [student, setStudent] = useState(null);
  const [error, setError] = useState('');
  const [isHovered, setIsHovered] = useState(false);

  useEffect(() => {
    const fetchStudentDetails = async () => {
      try {
        const response = await axios.post(
          'http://localhost:5000/api/student-details',
          {},
          { withCredentials: true }
        );

        if (response.data.success) {
          setStudent(response.data.student);
        } else {
          setError(response.data.message);
        }
      } catch (err) {
        console.error('Error fetching student details:', err);
        setError('Failed to fetch student details. Please try again.');
      }
    };

    fetchStudentDetails();
  }, []);

  // Handle hover effect
  const cardStyle = {
    borderRadius: '15px', // Increased border radius for a rounder look
    border: 'none',
    boxShadow: isHovered
      ? '0 8px 16px rgba(0, 0, 0, 0.2)'
      : '0 4px 8px rgba(0, 0, 0, 0.1)',
    backgroundColor: '#fff',
    padding: '30px', // Increased padding for a larger card
    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
    transform: isHovered ? 'scale(1.05)' : 'scale(1)',
    maxWidth: '1000px', // Increased max width for a larger card
    margin: '20px auto', // Added margin for better spacing
  };

  const containerStyle = {
    padding: '30px',
    maxWidth: '1000px', // Increased max width for the container
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif',
  };

  const errorStyle = {
    color: 'red',
    fontWeight: 'bold',
    textAlign: 'center',
  };

  const cardHeaderStyle = {
    backgroundColor: '#007BFF',
    color: '#fff',
    borderRadius: '15px 15px 0 0', // Rounded top corners for the header
    padding: '20px',
    textAlign: 'center',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  };

  const cardBodyStyle = {
    padding: '20px 0',
  };

  const rowStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    marginBottom: '20px', // Increased space between rows
  };

  const colStyle = {
    width: '48%',
  };

  const loadingStyle = {
    textAlign: 'center',
    fontSize: '18px',
    fontStyle: 'italic',
    color: '#007BFF',
  };

  const navBarStyle = {
    backgroundColor: '#333',
    padding: '10px 0',
    textAlign: 'center',
  };

  const navLinkStyle = {
    color: '#fff',
    textDecoration: 'none',
    margin: '0 15px',
    fontSize: '18px',
  };

  return (
    <div>
      {/* Navigation Bar */}
      <div style={navBarStyle}>
        <a href="/" style={navLinkStyle}>Home</a>
        <a href="/student-details" style={navLinkStyle}>Student Details</a>
        <a href="/contact" style={navLinkStyle}>Contact</a>
      </div>

      {/* Student Details Container */}
      <div style={containerStyle}>
        {error && <p style={errorStyle}>{error}</p>}
        {student ? (
          <div
            style={cardStyle}
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
          >
            <div style={cardHeaderStyle}>
              <h3>Student Profile</h3>
            </div>
            <div style={cardBodyStyle}>
              <div style={rowStyle}>
                <div style={colStyle}>
                  <strong>First Name:</strong>
                  <p>{student.firstName}</p>
                </div>
                <div style={colStyle}>
                  <strong>Last Name:</strong>
                  <p>{student.lastName}</p>
                </div>
              </div>
              <div style={rowStyle}>
                <div style={colStyle}>
                  <strong>Email:</strong>
                  <p>{student.email}</p>
                </div>
                <div style={colStyle}>
                  <strong>Age:</strong>
                  <p>{student.age}</p>
                </div>
              </div>
              <div style={rowStyle}>
                <div style={colStyle}>
                  <strong>Course:</strong>
                  <p>{student.course}</p>
                </div>
              </div>
            </div>
          </div>
        ) : (
          !error && <p style={loadingStyle}>Loading...</p>
        )}
      </div>
    </div>
  );
};

export default StudentDetails;
