import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddMarks = () => {
  const [students, setStudents] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState('');
  const [marks, setMarks] = useState({
    subject1: '',
    marks1: '',
    subject2: '',
    marks2: '',
    subject3: '',
    marks3: '',
    subject4: '',
    marks4: '',
    subject5: '',
    marks5: '',
    subject6: '',
    marks6: '',
  });
  const [error, setError] = useState('');

  // Fetch students from backend
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/students');
        setStudents(response.data); // Set the students in state
      } catch (err) {
        setError('Failed to fetch students.');
      }
    };
    fetchStudents();
  }, []);

  const handleStudentChange = (e) => {
    setSelectedStudent(e.target.value); // Update selected student
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMarks((prevMarks) => ({ ...prevMarks, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedStudent) {
      setError('Please select a student.');
      return;
    }

    try {
      const response = await axios.post(`http://localhost:5000/api/marks/${selectedStudent}`, marks);
      alert(response.data.message);  // Show success message
      setMarks({
        subject1: '',
        marks1: '',
        subject2: '',
        marks2: '',
        subject3: '',
        marks3: '',
        subject4: '',
        marks4: '',
        subject5: '',
        marks5: '',
        subject6: '',
        marks6: '',
      }); // Reset marks after submission
      setSelectedStudent('');  // Reset selected student
    } catch (err) {
      setError('Failed to add marks. Please try again.');
    }
  };

  return (
    <div>
      <h2>Enter Marks for Student</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <form onSubmit={handleSubmit}>
        <label>Select Student:</label>
        <select
          value={selectedStudent}
          onChange={handleStudentChange}
          required
        >
          <option value="">--Select a Student--</option>
          {students.map((student) => (
            <option key={student.id} value={student.id}>
              {student.firstName} {student.lastName} (Roll No: {student.rollNumber})
            </option>
          ))}
        </select>

        {[...Array(6).keys()].map((i) => (
          <div key={i}>
            <label>Subject {i + 1}</label>
            <input
              type="text"
              name={`subject${i + 1}`}
              value={marks[`subject${i + 1}`]}
              onChange={handleInputChange}
              placeholder={`Subject ${i + 1}`}
              required
            />
            <label>Marks</label>
            <input
              type="number"
              name={`marks${i + 1}`}
              value={marks[`marks${i + 1}`]}
              onChange={handleInputChange}
              placeholder={`Marks for Subject ${i + 1}`}
              required
            />
          </div>
        ))}
        <button type="submit">Save Marks</button>
      </form>
    </div>
  );
};

export default AddMarks;
