import React, { useState, useEffect } from 'react';
import axios from 'axios';


const StudentMarks = () => {
  const [marks, setMarks] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true); // Adding loading state

  useEffect(() => {
    const fetchMarks = async () => {
      try {
        setLoading(true); // Start loading

        const response = await axios.get('http://localhost:5000/api/student/marksviewstudent', {
          withCredentials: true, // Ensure session is sent along with the request
        });

        console.log('API Response:', response.data); // Log the full response data

        if (response.data.success) {
          setMarks(response.data.marks); // Set marks if successful
        } else {
          setError(response.data.message || 'Unknown error'); // Set error if any
        }
      } catch (error) {
        console.error('Error fetching marks:', error);
        setError('An error occurred while fetching marks. Please check the console for more details.');
      } finally {
        setLoading(false); // End loading
      }
    };

    fetchMarks();
  }, []);

  const parseMarks = (marksString) => {
    try {
      return JSON.parse(marksString);
    } catch (error) {
      console.error('Error parsing marks:', error);
      return {};
    }
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '30px', backgroundColor: '#f9f9f9' }}>
      <h2 style={{ textAlign: 'center', color: '#333', marginBottom: '20px' }}>Student Marks</h2>
      
      {loading && (
        <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
          <div
            style={{
              border: '4px solid #ddd',
              borderTop: '4px solid #3498db',
              borderRadius: '50%',
              width: '50px',
              height: '50px',
              animation: 'spin 1s linear infinite',
              opacity: loading ? 1 : 0,
              transition: 'opacity 0.3s ease-in-out',
            }}
          ></div>
        </div>
      )}

      {error && <p style={{ color: 'red', textAlign: 'center', marginTop: '20px' }}>{error}</p>}

      {!loading && !error && (
        <div>
          <h3 style={{ color: '#007bff', marginBottom: '20px' }}>Your Marks</h3>
          {marks && marks.length > 0 ? (
            marks.map((mark, index) => {
              const subjectMarks = parseMarks(mark.marks); // Parse marks JSON
              return (
                <div
                  key={index}
                  style={{
                    marginBottom: '30px',
                    padding: '20px',
                    backgroundColor: '#fff',
                    borderRadius: '8px',
                    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.transform = 'scale(1.05)';
                    e.currentTarget.style.boxShadow = '0 6px 12px rgba(0, 0, 0, 0.2)';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.transform = 'scale(1)';
                    e.currentTarget.style.boxShadow = '0 4px 8px rgba(0, 0, 0, 0.1)';
                  }}
                >
                  <h4 style={{ color: '#333', marginBottom: '10px' }}>{mark.semester}</h4>
                  <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                    <thead>
                      <tr style={{ backgroundColor: '#f1f1f1' }}>
                        <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Subject</th>
                        <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Marks</th>
                      </tr>
                    </thead>
                    <tbody>
                      {Object.keys(subjectMarks).map((subject, idx) => (
                        <tr
                          key={idx}
                          style={{
                            transition: 'background-color 0.3s',
                            cursor: 'pointer',
                          }}
                          onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = '#f9f9f9')}
                          onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = 'transparent')}
                        >
                          <td style={{ padding: '10px', border: '1px solid #ddd' }}>{subject}</td>
                          <td style={{ padding: '10px', border: '1px solid #ddd' }}>{subjectMarks[subject]}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              );
            })
          ) : (
            <p style={{ textAlign: 'center', marginTop: '20px' }}>No marks found.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default StudentMarks;
