import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentMarks = () => {
  const [marks, setMarks] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchMarks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/student/marksviewstudent', {
          withCredentials: true, // Ensure session is sent along with the request
        });

        console.log('API Response:', response.data); // Log the full response data

        if (response.data.success) {
          setMarks(response.data.marks);
        } else {
          setError(response.data.message || 'Unknown error');
        }
      } catch (error) {
        console.error('Error fetching marks:', error);
        setError('An error occurred while fetching marks. Please check the console for more details.');
      }
    };

    fetchMarks();
  }, []);

  if (!marks && !error) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>Student Marks</h2>
      {error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : (
        <div>
          <h3>Your Marks</h3>
          {marks && marks.length > 0 ? (
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr>
                  <th style={{ border: '1px solid #ddd', padding: '8px' }}>Subject</th>
                  <th style={{ border: '1px solid #ddd', padding: '8px' }}>Marks</th>
                </tr>
              </thead>
              <tbody>
                {Object.entries(marks).map(([subject, mark], index) => (
                  <tr key={index}>
                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>{subject}</td>
                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>{mark}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No marks found.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default StudentMarks;
