import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [error, setError] = useState(null);
  const [editingStudent, setEditingStudent] = useState(null);
  const [updatedStudent, setUpdatedStudent] = useState({
    rollNumber: '',
    firstName: '',
    lastName: '',
    email: '',
    age: '',
    course: ''
  });

  // Function to fetch students
  const fetchStudents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/studentsview');
      console.log('Students fetched:', response.data);
      setStudents(response.data.data); 
    } catch (err) {
      console.error('Error fetching students:', err);
      setError('Failed to fetch students. Please try again.');
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedStudent((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleUpdateStudent = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.put(
        `http://localhost:5000/api/students/${editingStudent.id}`,
        updatedStudent
      );
      console.log(response.data);
      alert('Student updated successfully');
      setEditingStudent(null); 
      setUpdatedStudent({
        rollNumber: '',
        firstName: '',
        lastName: '',
        email: '',
        age: '',
        course: ''
      });
      fetchStudents();
    } catch (err) {
      console.error('Error updating student:', err);
      alert('Failed to update student. Please try again.');
    }
  };

  const handleEdit = (student) => {
    setEditingStudent(student);
    setUpdatedStudent({
      rollNumber: student.rollNumber,
      firstName: student.firstName,
      lastName: student.lastName,
      email: student.email,
      age: student.age,
      course: student.course,
    });
  };

  const handleDeleteStudent = async (studentId) => {
    if (window.confirm('Are you sure you want to delete this student?')) {
      try {
        const response = await axios.delete(`http://localhost:5000/api/studentsdelete/${studentId}`);
        console.log('Delete response:', response.data);
        alert('Student deleted successfully');
        fetchStudents();
      } catch (err) {
        console.error('Error deleting student:', err.response ? err.response.data : err);
        alert('Failed to delete student. Please try again.');
      }
    }
  };

  const styles = {
    container: {
      padding: '30px 20px',
      maxWidth: '1200px',
      margin: '0 auto',
      backgroundColor: '#f4f4f4',
      borderRadius: '8px',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    },
    header: {
      fontSize: '2rem',
      fontWeight: 'bold',
      color: '#333',
      textAlign: 'center',
      marginBottom: '30px',
    },
    error: {
      color: 'red',
      fontWeight: 'bold',
      textAlign: 'center',
      marginBottom: '20px',
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
      border: '1px solid #ddd',
      padding: '20px',
      backgroundColor: '#fff',
      borderRadius: '8px',
      maxWidth: '600px',
      margin: '0 auto',
    },
    input: {
      padding: '10px',
      fontSize: '1rem',
      border: '1px solid #ccc',
      borderRadius: '4px',
      marginBottom: '15px',
    },
    button: {
      padding: '12px 20px',
      fontSize: '1rem',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
      transition: 'background-color 0.3s',
      margin: '10px 0',
    },
    buttonBlue: {
      backgroundColor: '#0066cc',
      color: 'white',
    },
    buttonBlueHover: {
      backgroundColor: '#004d99',
    },
    buttonRed: {
      backgroundColor: '#ff6f61',
      color: 'white',
    },
    buttonRedHover: {
      backgroundColor: '#ff4b3a',
    },
    table: {
      width: '100%',
      borderCollapse: 'collapse',
      marginTop: '30px',
    },
    tableHeader: {
      backgroundColor: '#007acc',
      color: '#fff',
      fontWeight: 'bold',
      textAlign: 'center',
      padding: '12px',
    },
    tableRow: {
      backgroundColor: '#fff',
      transition: 'background-color 0.3s',
    },
    tableRowHover: {
      backgroundColor: '#f1f1f1',
    },
    tableCell: {
      padding: '12px',
      textAlign: 'center',
      border: '1px solid #ddd',
    },
    actions: {
      display: 'flex',
      justifyContent: 'center',
      gap: '10px',
    },
  };

  return (
    <div className="student-list" style={styles.container}>
      <h2 style={styles.header}>Student List</h2>
      {error && <p style={styles.error}>{error}</p>}

      {editingStudent ? (
        <div className="edit-form" style={styles.form}>
          <h3>Edit Student</h3>
          <form onSubmit={handleUpdateStudent}>
            <input
              type="text"
              name="rollNumber"
              value={updatedStudent.rollNumber}
              onChange={handleInputChange}
              style={styles.input}
              required
            />
            <input
              type="text"
              name="firstName"
              value={updatedStudent.firstName}
              onChange={handleInputChange}
              style={styles.input}
              required
            />
            <input
              type="text"
              name="lastName"
              value={updatedStudent.lastName}
              onChange={handleInputChange}
              style={styles.input}
              required
            />
            <input
              type="email"
              name="email"
              value={updatedStudent.email}
              onChange={handleInputChange}
              style={styles.input}
              required
            />
            <input
              type="number"
              name="age"
              value={updatedStudent.age}
              onChange={handleInputChange}
              style={styles.input}
              required
            />
            <input
              type="text"
              name="course"
              value={updatedStudent.course}
              onChange={handleInputChange}
              style={styles.input}
              required
            />
            <button type="submit" style={{ ...styles.button, ...styles.buttonBlue }}>Update Student</button>
          </form>
          <button onClick={() => setEditingStudent(null)} style={{ ...styles.button, ...styles.buttonRed }}>Cancel</button>
        </div>
      ) : (
        <table style={styles.table} align="center">
          <thead>
            <tr style={styles.tableHeader}>
              <th style={styles.tableCell}>Roll Number</th>
              <th style={styles.tableCell}>First Name</th>
              <th style={styles.tableCell}>Last Name</th>
              <th style={styles.tableCell}>Email</th>
              <th style={styles.tableCell}>Age</th>
              <th style={styles.tableCell}>Course</th>
              <th style={styles.tableCell}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {students.length > 0 ? (
              students.map((student) => (
                <tr 
                  key={student.id} 
                  style={styles.tableRow} 
                  onMouseEnter={(e) => e.target.style.backgroundColor = styles.tableRowHover.backgroundColor} 
                  onMouseLeave={(e) => e.target.style.backgroundColor = ''}
                >
                  <td style={styles.tableCell}>{student.rollNumber}</td>
                  <td style={styles.tableCell}>{student.firstName}</td>
                  <td style={styles.tableCell}>{student.lastName}</td>
                  <td style={styles.tableCell}>{student.email}</td>
                  <td style={styles.tableCell}>{student.age}</td>
                  <td style={styles.tableCell}>{student.course}</td>
                  <td style={styles.tableCell}>
                    <div style={styles.actions}>
                      <button
                        onClick={() => handleEdit(student)}
                        style={{ ...styles.button, ...styles.buttonBlue }}
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDeleteStudent(student.id)}
                        style={{ ...styles.button, ...styles.buttonRed }}
                      >
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7" style={styles.tableCell}>No students available</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default StudentList;
