import axios from 'axios';
import { useEffect, useState } from 'react';

const StudentDetails = () => {
  const [student, setStudent] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchStudentDetails = async () => {
      try {
        const response = await axios.post(
          'http://localhost:5000/api/student-details',
          {}, // Adjust as needed
          { withCredentials: true }
        );

        if (response.data.success) {
          setStudent(response.data.student);
        } else {
          setError(response.data.message);
        }
      } catch (err) {
        console.error('Error fetching student details:', err);
        setError('Failed to fetch student details. Please try again.');
      }
    };

    fetchStudentDetails();
  }, []);

  return (
    <div className="container mt-5">
      {error && <p className="alert alert-danger">{error}</p>}
      {student ? (
        <div className="card shadow-lg p-4" style={styles.card}>
          <div className="card-header text-center bg-primary text-white" style={styles.cardHeader}>
            <h3>Student Profile</h3>
          </div>
          <div className="card-body">
            <div className="row">
              <div className="col-md-6 mb-3">
                <strong>First Name:</strong>
                <p>{student.firstName}</p>
              </div>
              <div className="col-md-6 mb-3">
                <strong>Last Name:</strong>
                <p>{student.lastName}</p>
              </div>
              <div className="col-md-6 mb-3">
                <strong>Email:</strong>
                <p>{student.email}</p>
              </div>
              <div className="col-md-6 mb-3">
                <strong>Age:</strong>
                <p>{student.age}</p>
              </div>
              <div className="col-md-12 mb-3">
                <strong>Course:</strong>
                <p>{student.course}</p>
              </div>
            </div>
          </div>
          <div className="card-footer text-center">
            <button className="btn btn-primary" style={styles.editButton}>Edit Details</button>
          </div>
        </div>
      ) : (
        !error && <p className="text-center">Loading...</p>
      )}
    </div>
  );
};

// Inline CSS Styles
const styles = {
  card: {
    borderRadius: '10px',
    border: 'none',
    backgroundColor: '#ffffff',
  },
  cardHeader: {
    backgroundColor: '#007BFF',
    color: 'white',
    borderRadius: '10px 10px 0 0',
    padding: '15px',
  },
  editButton: {
    borderRadius: '5px',
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#28a745',
    border: 'none',
    color: 'white',
    transition: 'background-color 0.3s ease',
  },
  editButtonHover: {
    backgroundColor: '#218838',
  },
};

export default StudentDetails;
