import React, { useState, useEffect } from 'react';

const AddMarks = () => {
  const [students, setStudents] = useState([]);
  const [semesters] = useState(['Semester 1', 'Semester 2', 'Semester 3', 'Semester 4']);
  const [subjects] = useState({
    'Semester 1': ['Mathematics', 'Physics', 'Chemistry'],
    'Semester 2': ['Biology', 'Computer Science', 'History'],
    'Semester 3': ['Economics', 'English Literature', 'Sociology'],
    'Semester 4': ['Geography', 'Political Science', 'Statistics'],
  });
  const [selectedStudent, setSelectedStudent] = useState('');
  const [selectedSemester, setSelectedSemester] = useState('');
  const [marks, setMarks] = useState({});
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/students');
        const data = await response.json();
        setStudents(data);
      } catch (err) {
        setError('Failed to fetch students.');
      }
    };
    fetchStudents();
  }, []);

  const handleStudentChange = (e) => {
    setSelectedStudent(e.target.value);
  };

  const handleSemesterChange = (e) => {
    setSelectedSemester(e.target.value);
    setMarks({});
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMarks((prevMarks) => ({ ...prevMarks, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedStudent || !selectedSemester) {
      setError('Please select both a student and a semester.');
      return;
    }

    const formattedMarks = subjects[selectedSemester].reduce((acc, subject, index) => {
      acc[subject] = marks[`marks${index + 1}`];
      return acc;
    }, {});

    const payload = {
      semester: selectedSemester,
      marks: formattedMarks,
    };

    try {
      const response = await fetch(`http://localhost:5000/api/marks/${selectedStudent}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      const data = await response.json();

      if (response.ok) {
        alert(data.message);
      } else {
        alert(data.error || 'Something went wrong.');
      }
    } catch (err) {
      alert('Failed to submit marks.');
    }
  };

  const styles = {
    container: {
      fontFamily: "'Poppins', sans-serif",
      color: "#fff",
      backgroundColor: "#1e1e2f",
      padding: "20px",
      borderRadius: "10px",
      maxWidth: "600px",
      margin: "0 auto",
      marginTop: "50px",
    },
    label: {
      color: "#f39c12",
      marginBottom: "10px",
      display: "block",
    },
    input: {
      width: "100%",
      padding: "10px",
      marginBottom: "15px",
      borderRadius: "8px",
      border: "1px solid #ccc",
      backgroundColor: "#25273d",
      color: "#fff",
    },
    button: {
      backgroundColor: "#f39c12",
      color: "#1e1e2f",
      padding: "10px 20px",
      border: "none",
      borderRadius: "8px",
      cursor: "pointer",
    },
    select: {
      width: "100%",
      padding: "10px",
      marginBottom: "15px",
      borderRadius: "8px",
      border: "1px solid #ccc",
      backgroundColor: "#25273d",
      color: "#fff",
    },
    error: {
      color: "red",
      marginBottom: "15px",
    },
  };

  return (
    <div style={styles.container}>
      <h2>Add Marks</h2>
      {error && <div style={styles.error}>{error}</div>}
      <form onSubmit={handleSubmit}>
        <label style={styles.label}>Select Student</label>
        <select
          value={selectedStudent}
          onChange={handleStudentChange}
          style={styles.select}
        >
          <option value="" disabled>
            -- Select a Student --
          </option>
          {students.map((student) => (
            <option key={student.id} value={student.id} style={{ color: "#000" }}>
              {student.name}
            </option>
          ))}
        </select>

        <label style={styles.label}>Select Semester</label>
        <select
          value={selectedSemester}
          onChange={handleSemesterChange}
          style={styles.select}
        >
          <option value="" disabled>
            -- Select a Semester --
          </option>
          {semesters.map((semester) => (
            <option key={semester} value={semester}>
              {semester}
            </option>
          ))}
        </select>

        {selectedSemester &&
          subjects[selectedSemester].map((subject, index) => (
            <div key={subject}>
              <label style={styles.label}>{subject}</label>
              <input
                type="number"
                name={`marks${index + 1}`}
                value={marks[`marks${index + 1}`] || ''}
                onChange={handleInputChange}
                style={styles.input}
                placeholder={`Enter marks for ${subject}`}
              />
            </div>
          ))}

        <button type="submit" style={styles.button}>
          Submit Marks
        </button>
      </form>
    </div>
  );
};

export default AddMarks;
