import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [error, setError] = useState(null);
  const [editingStudent, setEditingStudent] = useState(null);
  const [updatedStudent, setUpdatedStudent] = useState({
    rollNumber: '',
    firstName: '',
    lastName: '',
    email: '',
    age: '',
    course: ''
  });

  // Inline styles with Blue Theme
  const styles = {
    container: {
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f0f8ff',  // Light blue background
      borderRadius: '8px',
    },
    header: {
      textAlign: 'center',
      fontSize: '2rem',
      marginBottom: '20px',
      color: '#003366',  // Dark blue text
    },
    table: {
      width: '100%',
      borderCollapse: 'collapse',
      margin: '20px 0',
    },
    thTd: {
      padding: '10px',
      textAlign: 'center',
      border: '1px solid #ddd',
    },
    th: {
      backgroundColor: '#cce0ff',  // Lighter blue background
      fontWeight: 'bold',
      color: '#003366',
    },
    trEven: {
      backgroundColor: '#e6f2ff',  // Light blue rows
    },
    trHover: {
      backgroundColor: '#b3d1ff',  // Blue highlight on hover
    },
    button: {
      backgroundColor: '#0066cc',  // Blue button
      color: 'white',
      padding: '8px 15px',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      margin: '5px',
      transition: 'background-color 0.3s',
    },
    buttonHover: {
      backgroundColor: '#004d99',  // Darker blue button on hover
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
      border: '1px solid #ccc',
      padding: '20px',
      maxWidth: '500px',
      margin: '20px auto',
      backgroundColor: '#e6f2ff',  // Light blue background for form
      borderRadius: '8px',
    },
    input: {
      padding: '8px',
      fontSize: '1rem',
      border: '1px solid #ccc',
      borderRadius: '4px',
      marginBottom: '15px',
    },
    inputFocus: {
      borderColor: '#0066cc',  // Blue border on focus
      outline: 'none',
    },
    cancelButton: {
      backgroundColor: '#ff6f61',  // Red cancel button
      width: '100%',
    },
    cancelButtonHover: {
      backgroundColor: '#ff4b3a',
    },
    error: {
      color: 'red',
      fontSize: '1rem',
      textAlign: 'center',
      marginBottom: '20px',
    },
  };

  // Function to fetch students
  const fetchStudents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/studentsview');
      console.log('Students fetched:', response.data); // Log the response
      setStudents(response.data.data); // Set the students' data
    } catch (err) {
      console.error('Error fetching students:', err);  // Log the entire error
      setError('Failed to fetch students. Please try again.');
    }
  };

  // Fetch the students when the component mounts
  useEffect(() => {
    fetchStudents();
  }, []);

  // Handle student data change for updating
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedStudent((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handle form submission for updating student
  const handleUpdateStudent = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.put(
        `http://localhost:5000/api/students/${editingStudent.id}`,
        updatedStudent
      );
      console.log(response.data); // Log success message
      alert('Student updated successfully');
      setEditingStudent(null); // Close the update form
      setUpdatedStudent({
        rollNumber: '',
        firstName: '',
        lastName: '',
        email: '',
        age: '',
        course: ''
      });
      // Refresh the students list after update
      fetchStudents(); // Call the function to refresh the list
    } catch (err) {
      console.error('Error updating student:', err);
      alert('Failed to update student. Please try again.');
    }
  };

  // Edit student details
  const handleEdit = (student) => {
    setEditingStudent(student);
    setUpdatedStudent({
      rollNumber: student.rollNumber,
      firstName: student.firstName,
      lastName: student.lastName,
      email: student.email,
      age: student.age,
      course: student.course,
    });
  };

  const handleDeleteStudent = async (studentId) => {
    if (window.confirm('Are you sure you want to delete this student?')) {
      try {
        const response = await axios.delete(`http://localhost:5000/api/studentsdelete/${studentId}`);

        console.log('Delete response:', response.data); // Log the successful response
        alert('Student deleted successfully');
        // Refresh the students list after deletion
        fetchStudents(); // Call the function to refresh the list
      } catch (err) {
        console.error('Error deleting student:', err.response ? err.response.data : err);
        alert('Failed to delete student. Please try again.');
      }
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.header}>Student List</h2>
      {error && <p style={styles.error}>{error}</p>}

      {editingStudent ? (
        <div style={styles.form}>
          <h3>Edit Student</h3>
          <form onSubmit={handleUpdateStudent}>
            <div>
              <label>Roll Number:</label>
              <input
                type="text"
                name="rollNumber"
                value={updatedStudent.rollNumber}
                onChange={handleInputChange}
                style={styles.input}
                required
              />
            </div>
            <div>
              <label>First Name:</label>
              <input
                type="text"
                name="firstName"
                value={updatedStudent.firstName}
                onChange={handleInputChange}
                style={styles.input}
                required
              />
            </div>
            <div>
              <label>Last Name:</label>
              <input
                type="text"
                name="lastName"
                value={updatedStudent.lastName}
                onChange={handleInputChange}
                style={styles.input}
                required
              />
            </div>
            <div>
              <label>Email:</label>
              <input
                type="email"
                name="email"
                value={updatedStudent.email}
                onChange={handleInputChange}
                style={styles.input}
                required
              />
            </div>
            <div>
              <label>Age:</label>
              <input
                type="number"
                name="age"
                value={updatedStudent.age}
                onChange={handleInputChange}
                style={styles.input}
                required
              />
            </div>
            <div>
              <label>Course:</label>
              <input
                type="text"
                name="course"
                value={updatedStudent.course}
                onChange={handleInputChange}
                style={styles.input}
                required
              />
            </div>
            <button type="submit" style={styles.button}>Update Student</button>
          </form>
          <button onClick={() => setEditingStudent(null)} style={styles.cancelButton}>Cancel</button>
        </div>
      ) : (
        <table style={styles.table} align="center">
          <thead>
            <tr>
              <th style={{ ...styles.thTd, ...styles.th }}>Roll Number</th>
              <th style={styles.thTd}>First Name</th>
              <th style={styles.thTd}>Last Name</th>
              <th style={styles.thTd}>Email</th>
              <th style={styles.thTd}>Age</th>
              <th style={styles.thTd}>Course</th>
              <th style={styles.thTd}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {students.length > 0 ? (
              students.map((student) => (
                <tr key={student.id} style={styles.trHover}>
                  <td>{student.rollNumber}</td>
                  <td>{student.firstName}</td>
                  <td>{student.lastName}</td>
                  <td>{student.email}</td>
                  <td>{student.age}</td>
                  <td>{student.course}</td>
                  <td>
                    <button onClick={() => handleEdit(student)} style={styles.button}>Edit</button>
                    <button onClick={() => handleDeleteStudent(student.id)} style={styles.button}>Delete</button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7" style={styles.thTd}>No students available</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default StudentList;
