import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const AdminStudentList = () => {
  const [students, setStudents] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch the list of students
    axios.get('http://localhost:5000/api/studentsroll')
  .then((response) => {
    console.log(response.data);  // Log the response to check the data structure
    setStudents(response.data);
  })
  .catch((err) => {
    console.error('Error fetching students:', err);  // Log the error
    setError('Failed to fetch students');
  });

  }, []);
  
  return (
    <div>
      <h2>Student List</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <table>
        <thead>
          <tr>
            <th>Roll Number</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Age</th>
            <th>Course</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.length > 0 ? (
            students.map((student) => (
              <tr key={student.id}>
                <td>{student.rollNumber}</td>
                <td>{student.firstName}</td>
                <td>{student.lastName}</td>
                <td>{student.email}</td>
                <td>{student.age}</td>
                <td>{student.course}</td>
                <td>
                  <Link to={`/addMarks/${student.rollNumber}`}>
                    <button>Add Marks</button>
                  </Link>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7">No students available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default AdminStudentList;
