import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // to get route parameters
import axios from 'axios';

const AddMarks = () => {
  const { id } = useParams(); // Get student roll number from URL
  const [marks, setMarks] = useState({
    subject1: '',
    subject2: '',
    subject3: '',
    subject4: '',
    subject5: '',
    subject6: '',
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Fetch existing marks based on roll number (if applicable)
    const fetchMarks = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/Marks/${id}`);
        setMarks(response.data); // Assuming the data has fields subject1, subject2, etc.
      } catch (error) {
        setMessage('Failed to fetch marks.');
      }
    };

    fetchMarks();
  }, [id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMarks({
      ...marks,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post('http://localhost:5000/api/addMarks', {
        rollNumber: id,
        subjects: marks,
      });
      setMessage('Marks added successfully!');
    } catch (error) {
      setMessage('Failed to add marks.');
    }
  };

  return (
    <div>
      <h2>Add Marks for Student {id}</h2>
      <form onSubmit={handleSubmit}>
        {/* Render subject fields */}
        {Object.keys(marks).map((subject, index) => (
          <div key={index}>
            <label>{`Subject ${index + 1}:`}</label>
            <input
              type="text"
              name={subject}
              value={marks[subject]}
              onChange={handleInputChange}
              required
            />
          </div>
        ))}
        <button type="submit">Add Marks</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default AddMarks;
