import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentMarks = () => {
  const [marks, setMarks] = useState(null);
  const [semester, setSemester] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetching the logged-in student's marks
    axios.get('/api/student/marksviewstudent')
      .then((response) => {
        if (response.data.success) {
          const { student, marks } = response.data;
          setSemester(student.semester);  // Set semester info
          setMarks(marks);  // Set marks for the student
        } else {
          setError(response.data.message);
        }
      })
      .catch((err) => {
        console.error('Error fetching student marks:', err);
        setError('An error occurred while fetching marks.');
      });
  }, []);

  if (error) {
    return <div>{error}</div>;
  }

  if (!marks) {
    return <div>Loading marks...</div>;
  }

  return (
    <div>
      <h1>Semester: {semester}</h1>
      <table border="1">
        <thead>
          <tr>
            <th>Subject</th>
            <th>Marks</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(marks).map((subject) => (
            <tr key={subject}>
              <td>{subject}</td>
              <td>{marks[subject]}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StudentMarks;
