import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentMarks = () => {
  const [marks, setMarks] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchMarks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/student/marksviewstudent', {
          withCredentials: true, // Ensure session is sent along with the request
        });

        console.log('API Response:', response); // Log the full response

        if (response.data.success) {
          setMarks(response.data.marks);
        } else {
          setError(response.data.message || 'Unknown error');
        }
      } catch (error) {
        console.error('Error fetching marks:', error);
        setError('An error occurred while fetching marks. Please check the console for more details.');
      }
    };

    fetchMarks();
  }, []);

  if (!marks && !error) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>Student Marks</h2>
      {error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : (
        <div>
          <h3>Your Marks: {marks}</h3>
        </div>
      )}
    </div>
  );
};

export default StudentMarks;
