import React, { useEffect, useState } from "react";
import axios from "axios";

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [error, setError] = useState(null);
  const [editingStudent, setEditingStudent] = useState(null);
  const [updatedStudent, setUpdatedStudent] = useState({
    rollNumber: "",
    firstName: "",
    lastName: "",
    email: "",
    age: "",
    course: "",
  });

  const fetchStudents = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/studentsview");
      setStudents(response.data.data);
    } catch (err) {
      setError("Failed to fetch students. Please try again.");
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedStudent((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleUpdateStudent = async (e) => {
    e.preventDefault();
    try {
      await axios.put(
        `http://localhost:5000/api/students/${editingStudent.id}`,
        updatedStudent
      );
      alert("Student updated successfully");
      setEditingStudent(null);
      setUpdatedStudent({
        rollNumber: "",
        firstName: "",
        lastName: "",
        email: "",
        age: "",
        course: "",
      });
      fetchStudents();
    } catch (err) {
      alert("Failed to update student. Please try again.");
    }
  };

  const handleEdit = (student) => {
    setEditingStudent(student);
    setUpdatedStudent({
      rollNumber: student.rollNumber,
      firstName: student.firstName,
      lastName: student.lastName,
      email: student.email,
      age: student.age,
      course: student.course,
    });
  };

  const handleDeleteStudent = async (studentId) => {
    if (window.confirm("Are you sure you want to delete this student?")) {
      try {
        await axios.delete(`http://localhost:5000/api/studentsdelete/${studentId}`);
        alert("Student deleted successfully");
        fetchStudents();
      } catch (err) {
        alert("Failed to delete student. Please try again.");
      }
    }
  };

  return (
    <div
      style={{
        padding: "30px",
        fontFamily: "'Poppins', sans-serif",
        backgroundColor: "#1e1e2f",
        color: "#fff",
        minHeight: "100vh",
        maxWidth: "1200px",
        margin: "0 auto",
        borderRadius: "10px",
        boxShadow: "0 4px 10px rgba(0, 0, 0, 0.5)",
      }}
    >
      <h2
        style={{
          fontSize: "2rem",
          fontWeight: "bold",
          textAlign: "center",
          color: "#f39c12",
          marginBottom: "20px",
        }}
      >
        Student List
      </h2>
      {error && (
        <p
          style={{
            color: "#ff4b3a",
            fontWeight: "bold",
            textAlign: "center",
            marginBottom: "20px",
          }}
        >
          {error}
        </p>
      )}
      {editingStudent ? (
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            border: "1px solid #444",
            padding: "20px",
            backgroundColor: "#25273d",
            borderRadius: "8px",
            maxWidth: "600px",
            margin: "0 auto",
          }}
        >
          <h3
            style={{
              fontSize: "1.5rem",
              color: "#f39c12",
              marginBottom: "15px",
              textAlign: "center",
            }}
          >
            Edit Student
          </h3>
          <form onSubmit={handleUpdateStudent}>
            <input
              type="text"
              name="rollNumber"
              value={updatedStudent.rollNumber}
              onChange={handleInputChange}
              style={{
                padding: "10px",
                fontSize: "1rem",
                border: "1px solid #ccc",
                borderRadius: "4px",
                marginBottom: "15px",
                width: "100%",
              }}
              required
            />
            <input
              type="text"
              name="firstName"
              value={updatedStudent.firstName}
              onChange={handleInputChange}
              style={{
                padding: "10px",
                fontSize: "1rem",
                border: "1px solid #ccc",
                borderRadius: "4px",
                marginBottom: "15px",
                width: "100%",
              }}
              required
            />
            <input
              type="text"
              name="lastName"
              value={updatedStudent.lastName}
              onChange={handleInputChange}
              style={{
                padding: "10px",
                fontSize: "1rem",
                border: "1px solid #ccc",
                borderRadius: "4px",
                marginBottom: "15px",
                width: "100%",
              }}
              required
            />
            <input
              type="email"
              name="email"
              value={updatedStudent.email}
              onChange={handleInputChange}
              style={{
                padding: "10px",
                fontSize: "1rem",
                border: "1px solid #ccc",
                borderRadius: "4px",
                marginBottom: "15px",
                width: "100%",
              }}
              required
            />
            <input
              type="number"
              name="age"
              value={updatedStudent.age}
              onChange={handleInputChange}
              style={{
                padding: "10px",
                fontSize: "1rem",
                border: "1px solid #ccc",
                borderRadius: "4px",
                marginBottom: "15px",
                width: "100%",
              }}
              required
            />
            <input
              type="text"
              name="course"
              value={updatedStudent.course}
              onChange={handleInputChange}
              style={{
                padding: "10px",
                fontSize: "1rem",
                border: "1px solid #ccc",
                borderRadius: "4px",
                marginBottom: "15px",
                width: "100%",
              }}
              required
            />
            <button
              type="submit"
              style={{
                padding: "12px",
                fontSize: "1rem",
                backgroundColor: "#f39c12",
                color: "#1e1e2f",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
                width: "100%",
                marginBottom: "10px",
              }}
            >
              Update Student
            </button>
          </form>
          <button
            onClick={() => setEditingStudent(null)}
            style={{
              padding: "12px",
              fontSize: "1rem",
              backgroundColor: "#ff4b3a",
              color: "#fff",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
              width: "100%",
            }}
          >
            Cancel
          </button>
        </div>
      ) : (
        <table
          style={{
            width: "100%",
            borderCollapse: "collapse",
            marginTop: "30px",
          }}
        >
          <thead>
            <tr>
              <th style={{ padding: "12px", backgroundColor: "#007acc", color: "#fff" }}>
                Roll Number
              </th>
              {/* Add other headers similarly */}
            </tr>
          </thead>
          <tbody>
            {students.map((student) => (
              <tr key={student.id}>
                <td>{student.rollNumber}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default StudentList;
