import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Logout.css'; // Import the CSS file

const Logout = () => {
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/logout', {}, { withCredentials: true });

      console.log('Logout response:', response.data); // Log the response

      if (response.data.success) {
        // Clear localStorage
        localStorage.removeItem('role');
        localStorage.removeItem('user');

        // Redirect to login page
        navigate('/');
      } else {
        console.error('Logout failed:', response.data.message); // Log the error
      }
    } catch (error) {
      console.error('Logout error:', error); // Log the error
    }
  };

  return (
    <div className="card">
      <h3 className="title">Are you sure you want to logout?</h3>
      <div className="button-container">
        <button onClick={handleLogout} className="logout-button">
          Yes, Logout
        </button>
        <button onClick={() => navigate('/')} className="cancel-button">
          Cancel
        </button>
      </div>
    </div>
  );
};

export default Logout;
