import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentMarks = () => {
  const [marks, setMarks] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true); // Adding loading state

  useEffect(() => {
    const fetchMarks = async () => {
      try {
        // Start loading
        setLoading(true);

        // Sending the GET request with session support
        const response = await axios.get('http://localhost:5000/api/student/marksviewstudent', {
          withCredentials: true, // Ensure session is sent along with the request
        });

        console.log('API Response:', response.data); // Log the full response data

        // Check if the response is successful
        if (response.data.success) {
          const marksData = response.data.marks;
          // Parse the marks string into an object
          const parsedMarks = JSON.parse(marksData.marks);
          setMarks({ ...marksData, marks: parsedMarks }); // Set parsed marks
        } else {
          setError(response.data.message || 'Unknown error'); // Set error if any
        }
      } catch (error) {
        console.error('Error fetching marks:', error);
        setError('An error occurred while fetching marks. Please check the console for more details.');
      } finally {
        setLoading(false); // End loading
      }
    };

    fetchMarks();
  }, []); // Empty dependency array to run once after component mounts

  if (loading) {
    return <p>Loading...</p>; // Show loading message while fetching
  }

  return (
    <div>
      <h2>Student Marks</h2>
      {error ? (
        <p style={{ color: 'red' }}>{error}</p> // Display error if exists
      ) : (
        <div>
          <h3>Your Marks</h3>
          {marks ? (
            <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
              <thead>
                <tr style={{ backgroundColor: '#f4f4f4' }}>
                  <th style={{ padding: '10px', border: '1px solid #ddd' }}>Semester</th>
                  <th style={{ padding: '10px', border: '1px solid #ddd' }}>Subject</th>
                  <th style={{ padding: '10px', border: '1px solid #ddd' }}>Marks</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td rowSpan={Object.keys(marks.marks).length} style={{ padding: '10px', border: '1px solid #ddd' }}>
                    {marks.semester}
                  </td>
                  {Object.entries(marks.marks).map(([subject, mark], index) => (
                    <tr key={subject}>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>{subject}</td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>{mark}</td>
                    </tr>
                  ))}
                </tr>
              </tbody>
            </table>
          ) : (
            <p>No marks found.</p> // Handle case when no marks are available
          )}
        </div>
      )}
    </div>
  );
};

export default StudentMarks;
