import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [studentName, setStudentName] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (user && user.name) {
      setStudentName(user.name);
    } else {
      navigate("/login");
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  const styles = {
    container: {
      height: "100vh",
      display: "flex",
      flexDirection: "column",
      justifyContent: "center",
      alignItems: "center",
      background: "linear-gradient(45deg, #ffafbd, #ffc3a0)",
      backgroundSize: "400% 400%",
      animation: "gradientAnimation 8s ease infinite",
      fontFamily: "'Poppins', sans-serif",
      textAlign: "center",
    },
    card: {
      background: "#f5f5f5",
      borderRadius: "20px",
      padding: "30px",
      boxShadow:
        "8px 8px 15px rgba(0, 0, 0, 0.2), -8px -8px 15px rgba(255, 255, 255, 0.7)",
      maxWidth: "600px",
      width: "90%",
    },
    title: {
      color: "#333",
      fontSize: "28px",
      fontWeight: "bold",
      marginBottom: "20px",
    },
    subtitle: {
      color: "#666",
      fontSize: "18px",
      marginBottom: "30px",
    },
    button: {
      padding: "12px 20px",
      background: "linear-gradient(45deg, #ff7e5f, #feb47b)",
      color: "white",
      fontSize: "16px",
      fontWeight: "bold",
      border: "none",
      borderRadius: "8px",
      cursor: "pointer",
      transition: "transform 0.3s ease, box-shadow 0.3s ease",
    },
    buttonHover: {
      transform: "scale(1.05)",
      boxShadow: "0 5px 15px rgba(255, 94, 58, 0.3)",
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h1 style={styles.title}>Welcome, {studentName}!</h1>
        <p style={styles.subtitle}>This is your student homepage. Explore and learn!</p>
        <button
          style={styles.button}
          onClick={handleLogout}
          onMouseOver={(e) => Object.assign(e.target.style, styles.buttonHover)}
          onMouseOut={(e) => Object.assign(e.target.style, { transform: "", boxShadow: "" })}
        >
          Log Out
        </button>
      </div>
    </div>
  );
};

export default Home;
