import React, { useState, useEff } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const AddMarks = () => {
  const { rollNumber } = useParams();
  const navigate = useNavigate();
  const [marks, setMarks] = useState({
    subject1: '',
    marks1: '',
    subject2: '',
    marks2: '',
    subject3: '',
    marks3: '',
    subject4: '',
    marks4: '',
    subject5: '',
    marks5: '',
    subject6: '',
    marks6: '',
  });
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMarks((prevMarks) => ({ ...prevMarks, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:5000/api/marks/${rollNumber}`, marks);
      alert(response.data.message);  // Show success message
      navigate('/');  // Redirect to the student list or another page
    } catch (err) {
      setError('Failed to add marks. Please try again.');
    }
  };

  return (
    <div>
      <h2>Enter Marks for Student {rollNumber}</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      
      <form onSubmit={handleSubmit}>
        {[...Array(6).keys()].map((i) => (
          <div key={i}>
            <label>Subject {i + 1}</label>
            <input
              type="text"
              name={`subject${i + 1}`}
              value={marks[`subject${i + 1}`]}
              onChange={handleInputChange}
              placeholder={`Subject ${i + 1}`}
              required
            />
            <label>Marks</label>
            <input
              type="number"
              name={`marks${i + 1}`}
              value={marks[`marks${i + 1}`]}
              onChange={handleInputChange}
              placeholder={`Marks for Subject ${i + 1}`}
              required
            />
          </div>
        ))}
        <button type="submit">Save Marks</button>
      </form>
    </div>
  );
};

export default AddMarks;
