import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const AddMarks = () => {
  const { rollNumber } = useParams();  // Get rollNumber from URL params
  const navigate = useNavigate();  // Use useNavigate hook
  const [marks, setMarks] = useState({
    semester: '',
    subject1: '',
    subject2: '',
    subject3: '',
    subject4: '',
    subject5: '',
    subject6: ''
  });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setMarks({ ...marks, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Send marks to the backend
    axios.post('http://localhost:5000/api/addMarks', { rollNumber, ...marks })
      .then((response) => {
        alert(response.data.message);
        navigate('/admin_home');  // Redirect to admin home after successful submission
      })
      .catch((err) => {
        setError('Failed to add marks');
      });
  };

  return (
    <div>
      <h2>Add Marks for {rollNumber}</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Semester:</label>
          <select name="semester" value={marks.semester} onChange={handleChange} required>
            <option value="">Select Semester</option>
            {[1, 2, 3, 4, 5, 6].map((sem) => (
              <option key={sem} value={sem}>{sem}</option>
            ))}
          </select>
        </div>
        <div>
          <label>Subject 1:</label>
          <input type="number" name="subject1" value={marks.subject1} onChange={handleChange} required />
        </div>
        <div>
          <label>Subject 2:</label>
          <input type="number" name="subject2" value={marks.subject2} onChange={handleChange} required />
        </div>
        <div>
          <label>Subject 3:</label>
          <input type="number" name="subject3" value={marks.subject3} onChange={handleChange} required />
        </div>
        <div>
          <label>Subject 4:</label>
          <input type="number" name="subject4" value={marks.subject4} onChange={handleChange} required />
        </div>
        <div>
          <label>Subject 5:</label>
          <input type="number" name="subject5" value={marks.subject5} onChange={handleChange} required />
        </div>
        <div>
          <label>Subject 6:</label>
          <input type="number" name="subject6" value={marks.subject6} onChange={handleChange} required />
        </div>
        <button type="submit">Submit Marks</button>
      </form>
    </div>
  );
};

export default AddMarks;
