import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentMarks = () => {
  const [marks, setMarks] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true); // Adding loading state

  useEffect(() => {
    const fetchMarks = async () => {
      try {
        // Start loading
        setLoading(true);

        // Sending the GET request with session support
        const response = await axios.get('http://localhost:5000/api/student/marksviewstudent', {
          withCredentials: true, // Ensure session is sent along with the request
        });

        console.log('API Response:', response.data); // Log the full response data

        // Check if the response is successful
        if (response.data.success) {
          setMarks(response.data.marks); // Set marks if successful
        } else {
          setError(response.data.message || 'Unknown error'); // Set error if any
        }
      } catch (error) {
        console.error('Error fetching marks:', error);
        setError('An error occurred while fetching marks. Please check the console for more details.');
      } finally {
        setLoading(false); // End loading
      }
    };

    fetchMarks();
  }, []); // Empty dependency array to run once after component mounts

  if (loading) {
    return <p>Loading...</p>; // Show loading message while fetching
  }

  // Function to convert marks from string to JSON object
  const parseMarks = (marksString) => {
    try {
      return JSON.parse(marksString);
    } catch (error) {
      console.error('Error parsing marks:', error);
      return {};
    }
  };

  return (
    <div>
      <h2>Student Marks</h2>
      {error ? (
        <p style={{ color: 'red' }}>{error}</p> // Display error if exists
      ) : (
        <div>
          <h3>Your Marks</h3>
          {marks && marks.length > 0 ? (
            marks.map((mark, index) => {
              const subjectMarks = parseMarks(mark.marks); // Parse marks JSON
              return (
                <div key={index} style={{ marginBottom: '20px' }}>
                  <h4>{mark.semester}</h4>
                  <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                    <thead>
                      <tr>
                        <th style={{ border: '1px solid #ddd', padding: '8px' }}>Subject</th>
                        <th style={{ border: '1px solid #ddd', padding: '8px' }}>Marks</th>
                      </tr>
                    </thead>
                    <tbody>
                      {Object.keys(subjectMarks).map((subject, idx) => (
                        <tr key={idx}>
                          <td style={{ border: '1px solid #ddd', padding: '8px' }}>{subject}</td>
                          <td style={{ border: '1px solid #ddd', padding: '8px' }}>{subjectMarks[subject]}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              );
            })
          ) : (
            <p>No marks found.</p> // Handle case when no marks are available
          )}
        </div>
      )}
    </div>
  );
};

export default StudentMarks;
