import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [error, setError] = useState(null);

  // Function to fetch students
  const fetchStudents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/studentsroll');
      console.log('Students fetched:', response.data); // Log the response
      setStudents(response.data); // Set the students' data
    } catch (err) {
      console.error('Error fetching students:', err);  // Log the entire error
      setError('Failed to fetch students. Please try again.');
    }
  };

  // Fetch the students when the component mounts
  useEffect(() => {
    fetchStudents();
  }, []);

  return (
    <div className="student-list">
      <h2>Student List</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <table align="center">
        <thead>
          <tr>
            <th>Roll Number</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Age</th>
            <th>Course</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.length > 0 ? (
            students.map((student) => (
              <tr key={student.id}>
                <td>{student.rollNumber}</td>
                <td>{student.firstName}</td>
                <td>{student.lastName}</td>
                <td>{student.email}</td>
                <td>{student.age}</td>
                <td>{student.course}</td>
                <td>
                  {/* Add Marks button */}
                  <Link to={`/addMarks/${student.rollNumber}`}>
                    <button>Add Marks</button>
                  </Link>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7">No students available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default StudentList;
