import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [error, setError] = useState(null);
  const [editingStudent, setEditingStudent] = useState(null);
  const [updatedStudent, setUpdatedStudent] = useState({
    rollNumber: '',
    firstName: '',
    lastName: '',
    email: '',
    age: '',
    course: ''
  });

  // Function to fetch students
  const fetchStudents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/studentsview');
      console.log('Students fetched:', response.data); // Log the response
      setStudents(response.data.data); // Set the students' data
    } catch (err) {
      console.error('Error fetching students:', err);  // Log the entire error
      setError('Failed to fetch students. Please try again.');
    }
  };

  // Fetch the students when the component mounts
  useEffect(() => {
    fetchStudents();
  }, []);

  // Handle student data change for updating
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedStudent((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handle form submission for updating student
  const handleUpdateStudent = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.put(
        `http://localhost:5000/api/students/${editingStudent.id}`,
        updatedStudent
      );
      console.log(response.data); // Log success message
      alert('Student updated successfully');
      setEditingStudent(null); // Close the update form
      setUpdatedStudent({
        rollNumber: '',
        firstName: '',
        lastName: '',
        email: '',
        age: '',
        course: ''
      });
      // Refresh the students list after update
      fetchStudents(); // Call the function to refresh the list
    } catch (err) {
      console.error('Error updating student:', err);
      alert('Failed to update student. Please try again.');
    }
  };

  // Edit student details
  const handleEdit = (student) => {
    setEditingStudent(student);
    setUpdatedStudent({
      rollNumber: student.rollNumber,
      firstName: student.firstName,
      lastName: student.lastName,
      email: student.email,
      age: student.age,
      course: student.course,
    });
  };

  const handleDeleteStudent = async (studentId) => {
    if (window.confirm('Are you sure you want to delete this student?')) {
      try {
        const response = await axios.delete(`http://localhost:5000/api/studentsdelete/${studentId}`);

        console.log('Delete response:', response.data); // Log the successful response
        alert('Student deleted successfully');
        // Refresh the students list after deletion
        fetchStudents(); // Call the function to refresh the list
      } catch (err) {
        console.error('Error deleting student:', err.response ? err.response.data : err);
        alert('Failed to delete student. Please try again.');
      }
    }
  };

  return (
    <div style={{ maxWidth: '900px', margin: '0 auto', padding: '20px', backgroundColor: '#f9f9f9', borderRadius: '8px' }}>
      <h2 style={{ fontSize: '24px', fontWeight: '600', color: '#333', textAlign: 'center' }}>Student List</h2>
      {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}

      {editingStudent ? (
        <div style={{ padding: '20px', backgroundColor: '#fff', borderRadius: '8px', boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)' }}>
          <h3 style={{ fontSize: '20px', fontWeight: '600', color: '#333', textAlign: 'center' }}>Edit Student</h3>
          <form onSubmit={handleUpdateStudent}>
            <div style={{ marginBottom: '15px' }}>
              <label style={{ fontSize: '16px', fontWeight: '500', color: '#555' }}>Roll Number:</label>
              <input
                type="text"
                name="rollNumber"
                value={updatedStudent.rollNumber}
                onChange={handleInputChange}
                required
                style={{
                  width: '100%',
                  padding: '10px',
                  fontSize: '16px',
                  border: '1px solid #ddd',
                  borderRadius: '6px',
                  outline: 'none',
                }}
              />
            </div>
            <div style={{ marginBottom: '15px' }}>
              <label style={{ fontSize: '16px', fontWeight: '500', color: '#555' }}>First Name:</label>
              <input
                type="text"
                name="firstName"
                value={updatedStudent.firstName}
                onChange={handleInputChange}
                required
                style={{
                  width: '100%',
                  padding: '10px',
                  fontSize: '16px',
                  border: '1px solid #ddd',
                  borderRadius: '6px',
                  outline: 'none',
                }}
              />
            </div>
            <div style={{ marginBottom: '15px' }}>
              <label style={{ fontSize: '16px', fontWeight: '500', color: '#555' }}>Last Name:</label>
              <input
                type="text"
                name="lastName"
                value={updatedStudent.lastName}
                onChange={handleInputChange}
                required
                style={{
                  width: '100%',
                  padding: '10px',
                  fontSize: '16px',
                  border: '1px solid #ddd',
                  borderRadius: '6px',
                  outline: 'none',
                }}
              />
            </div>
            <div style={{ marginBottom: '15px' }}>
              <label style={{ fontSize: '16px', fontWeight: '500', color: '#555' }}>Email:</label>
              <input
                type="email"
                name="email"
                value={updatedStudent.email}
                onChange={handleInputChange}
                required
                style={{
                  width: '100%',
                  padding: '10px',
                  fontSize: '16px',
                  border: '1px solid #ddd',
                  borderRadius: '6px',
                  outline: 'none',
                }}
              />
            </div>
            <div style={{ marginBottom: '15px' }}>
              <label style={{ fontSize: '16px', fontWeight: '500', color: '#555' }}>Age:</label>
              <input
                type="number"
                name="age"
                value={updatedStudent.age}
                onChange={handleInputChange}
                required
                style={{
                  width: '100%',
                  padding: '10px',
                  fontSize: '16px',
                  border: '1px solid #ddd',
                  borderRadius: '6px',
                  outline: 'none',
                }}
              />
            </div>
            <div style={{ marginBottom: '15px' }}>
              <label style={{ fontSize: '16px', fontWeight: '500', color: '#555' }}>Course:</label>
              <input
                type="text"
                name="course"
                value={updatedStudent.course}
                onChange={handleInputChange}
                required
                style={{
                  width: '100%',
                  padding: '10px',
                  fontSize: '16px',
                  border: '1px solid #ddd',
                  borderRadius: '6px',
                  outline: 'none',
                }}
              />
            </div>
            <button
              type="submit"
              style={{
                padding: '10px 20px',
                backgroundColor: '#007bff',
                color: '#fff',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                transition: 'background-color 0.3s ease'
              }}
            >
              Update Student
            </button>
          </form>
          <button
            onClick={() => setEditingStudent(null)}
            style={{
              marginTop: '10px',
              padding: '10px 20px',
              backgroundColor: '#ccc',
              color: '#fff',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
            }}
          >
            Cancel
          </button>
        </div>
      ) : (
        <table style={{ width: '100%', marginTop: '20px', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th style={{ padding: '10px', backgroundColor: '#f4f4f4', textAlign: 'left' }}>Roll Number</th>
              <th style={{ padding: '10px', backgroundColor: '#f4f4f4', textAlign: 'left' }}>First Name</th>
              <th style={{ padding: '10px', backgroundColor: '#f4f4f4', textAlign: 'left' }}>Last Name</th>
              <th style={{ padding: '10px', backgroundColor: '#f4f4f4', textAlign: 'left' }}>Email</th>
              <th style={{ padding: '10px', backgroundColor: '#f4f4f4', textAlign: 'left' }}>Age</th>
              <th style={{ padding: '10px', backgroundColor: '#f4f4f4', textAlign: 'left' }}>Course</th>
              <th style={{ padding: '10px', backgroundColor: '#f4f4f4', textAlign: 'left' }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {students.length > 0 ? (
              students.map((student) => (
                <tr key={student.id}>
                  <td style={{ padding: '10px', textAlign: 'left' }}>{student.rollNumber}</td>
                  <td style={{ padding: '10px', textAlign: 'left' }}>{student.firstName}</td>
                  <td style={{ padding: '10px', textAlign: 'left' }}>{student.lastName}</td>
                  <td style={{ padding: '10px', textAlign: 'left' }}>{student.email}</td>
                  <td style={{ padding: '10px', textAlign: 'left' }}>{student.age}</td>
                  <td style={{ padding: '10px', textAlign: 'left' }}>{student.course}</td>
                  <td style={{ padding: '10px', textAlign: 'left' }}>
                    <button
                      onClick={() => handleEdit(student)}
                      style={{
                        padding: '6px 12px',
                        backgroundColor: '#007bff',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        marginRight: '5px',
                      }}
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDeleteStudent(student.id)}
                      style={{
                        padding: '6px 12px',
                        backgroundColor: '#dc3545',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer',
                      }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7" style={{ textAlign: 'center', padding: '20px' }}>No students available</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default StudentList;
