import React, { useState } from 'react';
import axios from 'axios';

const StudentList = () => {
  const [rollNumber, setRollNumber] = useState('');
  const [semester, setSemester] = useState('');
  const [subjects, setSubjects] = useState({
    subject1: '',
    subject2: '',
    subject3: '',
    subject4: '',
    subject5: '',
    subject6: '',
  });
  const [message, setMessage] = useState('');

  const semesters = ['Semester 1', 'Semester 2', 'Semester 3', 'Semester 4']; // Example semesters
  const subjectsList = ['Math', 'Science', 'English', 'History', 'Geography', 'Computer Science']; // Example subjects

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'semester') {
      setSemester(value);
    } else if (name.startsWith('subject')) {
      setSubjects({
        ...subjects,
        [name]: value,
      });
    } else {
      setRollNumber(value);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      // Send the data to the backend
      await axios.post('http://localhost:5000/api/addMarks', {
        rollNumber,
        semester,
        subjects,
      });
      setMessage('Marks added successfully!');
    } catch (error) {
      setMessage('Failed to add marks.');
    }
  };
  

  return (
    <div>
      <h2>Add Marks</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Roll Number:</label>
          <input
            type="text"
            name="rollNumber"
            value={rollNumber}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Semester:</label>
          <select name="semester" value={semester} onChange={handleInputChange} required>
            <option value="">Select Semester</option>
            {semesters.map((sem, index) => (
              <option key={index} value={sem}>
                {sem}
              </option>
            ))}
          </select>
        </div>
        <div>
          {Object.keys(subjects).map((subject, index) => (
            <div key={index}>
              <label>{`Subject ${index + 1}:`}</label>
              <select
                name={subject}
                value={subjects[subject]}
                onChange={handleInputChange}
                required
              >
                <option value="">Select Subject</option>
                {subjectsList.map((sub, index) => (
                  <option key={index} value={sub}>
                    {sub}
                  </option>
                ))}
              </select>
            </div>
          ))}
        </div>
        <button type="submit">Add Marks</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default StudentList;
