import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
  
    try {
      const response = await axios.post(
        'http://localhost:5000/api/login',
        { email, password },
        { withCredentials: true }
      );
  
      console.log('Login response:', response.data); // Log the response
  
      if (response.data.success) {
        localStorage.setItem('role', response.data.role);
        localStorage.setItem('user', JSON.stringify(response.data.student || {}));
  
        if (response.data.role === 'admin') {
          navigate('/admin_home');
        } else if (response.data.role === 'student') {
          navigate('/StudentHome');
        }
      } else {
        console.log('Backend error:', response.data.message); // Log the backend error
        setError(response.data.message);
      }
    } catch (error) {
      console.error('Login error:', error); // Log the error details
      setError('An error occurred. Please try again later.');
    }
  };
  
  return (
    <div style={{ backgroundColor: '#f4f7fc', display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{
        backgroundColor: '#fff',
        padding: '30px',
        borderRadius: '10px',
        boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
        width: '100%',
        maxWidth: '400px',
        textAlign: 'center'
      }}>
        <h2 style={{ fontSize: '28px', color: '#333', fontWeight: '600', marginBottom: '30px' }}>Login</h2>
        <form onSubmit={handleLogin}>
          <div style={{ marginBottom: '15px', position: 'relative' }}>
            <label style={{ fontSize: '16px', fontWeight: '500', color: '#555', display: 'block', marginBottom: '5px' }}>Email:</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              style={{
                width: '100%',
                padding: '12px 15px',
                border: '1px solid #ddd',
                borderRadius: '8px',
                fontSize: '16px',
                color: '#333',
                transition: 'border-color 0.3s ease'
              }}
            />
          </div>
          <div style={{ marginBottom: '15px', position: 'relative' }}>
            <label style={{ fontSize: '16px', fontWeight: '500', color: '#555', display: 'block', marginBottom: '5px' }}>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              style={{
                width: '100%',
                padding: '12px 15px',
                border: '1px solid #ddd',
                borderRadius: '8px',
                fontSize: '16px',
                color: '#333',
                transition: 'border-color 0.3s ease'
              }}
            />
          </div>
          {error && <p style={{ color: 'red', fontSize: '14px', marginTop: '10px' }}>{error}</p>}
          <button
            type="submit"
            style={{
              width: '100%',
              padding: '12px',
              backgroundColor: '#007bff',
              color: '#fff',
              fontSize: '16px',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer',
              transition: 'background-color 0.3s ease'
            }}
            onMouseEnter={(e) => (e.target.style.backgroundColor = '#0056b3')}
            onMouseLeave={(e) => (e.target.style.backgroundColor = '#007bff')}
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
