import React, { useEffect, useState } from 'react';
import axios from 'axios';

const StudentDetails = () => {
  const [student, setStudent] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStudentDetails = async () => {
      try {
        // Retrieve email from localStorage
        const user = localStorage.getItem('user');
        if (!user) {
          setError('No user is logged in.');
          setLoading(false);
          return;
        }

        const email = JSON.parse(user).email;

        // API call to fetch student details
        const response = await axios.post(
          'http://localhost:5000/api/student-details',
          { email }
        );

        if (response.data.success) {
          setStudent(response.data.student);
        } else {
          setError(response.data.message || 'Failed to fetch student details.');
        }
      } catch (error) {
        console.error('Error fetching student details:', error);
        setError('An error occurred while fetching student details.');
      } finally {
        setLoading(false);
      }
    };

    fetchStudentDetails();
  }, []);

  if (loading) {
    return <p>Loading student details...</p>;
  }

  if (error) {
    return <p style={{ color: 'red', fontWeight: 'bold' }}>{error}</p>;
  }

  if (!student) {
    return <p>No student details available.</p>;
  }

  return (
    <div>
      <h2>Student Details</h2>
      <table style={{ border: '1px solid black', width: '50%', margin: '0 auto' }}>
        <tbody>
          <tr>
            <td style={{ fontWeight: 'bold', padding: '10px' }}>First Name</td>
            <td style={{ padding: '10px' }}>{student.firstName}</td>
          </tr>
          <tr>
            <td style={{ fontWeight: 'bold', padding: '10px' }}>Last Name</td>
            <td style={{ padding: '10px' }}>{student.lastName}</td>
          </tr>
          <tr>
            <td style={{ fontWeight: 'bold', padding: '10px' }}>Email</td>
            <td style={{ padding: '10px' }}>{student.email}</td>
          </tr>
          <tr>
            <td style={{ fontWeight: 'bold', padding: '10px' }}>Age</td>
            <td style={{ padding: '10px' }}>{student.age}</td>
          </tr>
          <tr>
            <td style={{ fontWeight: 'bold', padding: '10px' }}>Course</td>
            <td style={{ padding: '10px' }}>{student.course}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default StudentDetails;
