import React from 'react';
import { Link } from 'react-router-dom';

const StudentHomePage = () => {
  // Inline Styles
  const containerStyle = {
    fontFamily: 'Arial, sans-serif',
    padding: '30px',
    background: 'linear-gradient(to right, #6a11cb, #2575fc)', // Soft blue-purple gradient
    minHeight: '100vh',
    color: '#fff',
  };

  const headerStyle = {
    textAlign: 'center',
    color: '#fff',
    fontSize: '36px',
    fontWeight: 'bold',
    marginBottom: '20px',
    textTransform: 'uppercase',
    letterSpacing: '2px',
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.3)', // Subtle text shadow for emphasis
  };

  const navBarStyle = {
    display: 'flex',
    justifyContent: 'center',
    backgroundColor: '#2c3e50', // Darker blue background for navbar
    padding: '15px 0',
    borderRadius: '50px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    marginBottom: '30px',
    transition: 'background-color 0.3s ease',
  };

  const linkStyle = {
    textDecoration: 'none',
    color: 'white',
    fontWeight: 'bold',
    margin: '0 20px',
    fontSize: '18px',
    transition: 'transform 0.3s ease, color 0.3s ease',
  };

  const linkHoverStyle = {
    transform: 'scale(1.1)',
    color: '#1abc9c', // Light turquoise accent color for hover
  };

  const sectionStyle = {
    textAlign: 'center',
    marginBottom: '40px',
  };

  const footerStyle = {
    textAlign: 'center',
    color: '#ddd',
    fontSize: '14px',
    borderTop: '1px solid #444',
    paddingTop: '20px',
    marginTop: '40px',
  };

  const dashboardStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(3, 1fr)',
    gap: '20px',
    marginBottom: '40px',
  };

  const cardStyle = {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
    textAlign: 'center',
    transition: 'transform 0.3s ease, box-shadow 0.3s ease, background-color 0.3s ease',
    cursor: 'pointer',
  };

  const cardHoverStyle = {
    transform: 'scale(1.05)',
    boxShadow: '0 6px 16px rgba(0, 0, 0, 0.2)',
    backgroundColor: '#f0f8ff', // Light blue background on hover
  };

  const cardHeaderStyle = {
    fontSize: '24px',
    color: '#2575fc', // Blue color for card header
    marginBottom: '15px',
    textTransform: 'uppercase',
    letterSpacing: '1px',
  };

  const cardContentStyle = {
    fontSize: '18px',
    color: '#555',
    marginBottom: '20px',
  };

  return (
    <div style={containerStyle}>
      <header style={headerStyle}>
        Student Dashboard
      </header>

      <div style={navBarStyle}>
        <Link to="/profile" style={linkStyle} className="nav-link">
          Profile
        </Link>
        <Link to="/courses" style={linkStyle} className="nav-link">
          Courses
        </Link>
        <Link to="/assignments" style={linkStyle} className="nav-link">
          Assignments
        </Link>
        <Link to="/grades" style={linkStyle} className="nav-link">
          Grades
        </Link>
        <Link to="/logout" style={linkStyle} className="nav-link">
          Logout
        </Link>
      </div>

      <section style={sectionStyle}>
        <h2>Dashboard</h2>
        <p>Quick overview of your current status and activities.</p>
      </section>

      <section style={dashboardStyle}>
        <div style={cardStyle} className="card">
          <h3 style={cardHeaderStyle}>Courses</h3>
          <p style={cardContentStyle}>Manage and view your enrolled courses.</p>
          <Link to="/courses" style={{ textDecoration: 'none', color: '#2575fc' }}>
            View Courses
          </Link>
        </div>

        <div style={cardStyle} className="card">
          <h3 style={cardHeaderStyle}>Assignments</h3>
          <p style={cardContentStyle}>Check your upcoming and completed assignments.</p>
          <Link to="/assignments" style={{ textDecoration: 'none', color: '#2575fc' }}>
            View Assignments
          </Link>
        </div>

        <div style={cardStyle} className="card">
          <h3 style={cardHeaderStyle}>Grades</h3>
          <p style={cardContentStyle}>View your grades and progress in courses.</p>
          <Link to="/grades" style={{ textDecoration: 'none', color: '#2575fc' }}>
            View Grades
          </Link>
        </div>
      </section>

      <section style={sectionStyle}>
        <h2>Important Announcements</h2>
        <p>Stay updated with the latest news and announcements from your professors.</p>
        <ul>
          <li>New Assignment on Data Structures (Due: 20th Dec)</li>
          <li>Upcoming Exam Schedule Released</li>
          <li>Semester Break Starts on 1st Jan</li>
        </ul>
      </section>

      <footer style={footerStyle}>
        <p>&copy; 2024 Student Portal. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default StudentHomePage;
