const styles = {
  container: {
    fontFamily: "'Poppins', sans-serif",
    color: "#fff",
    backgroundColor: "#1e1e2f",
    padding: "20px",
    borderRadius: "10px",
    maxWidth: "600px",
    margin: "0 auto",
    marginTop: "50px",
  },
  label: {
    color: "#f39c12",
    marginBottom: "10px",
    display: "block",
  },
  input: {
    width: "100%",
    padding: "10px",
    marginBottom: "15px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    backgroundColor: "#25273d",
    color: "#fff",
  },
  button: {
    backgroundColor: "#f39c12",
    color: "#1e1e2f",
    padding: "10px 20px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    transition: "all 0.3s ease-in-out",
  },
  buttonHover: {
    backgroundColor: "#1e1e2f",
    color: "#f39c12",
    padding: "10px 20px",
    border: "1px solid #f39c12",
    borderRadius: "8px",
    cursor: "pointer",
    transition: "all 0.3s ease-in-out",
  },
  select: {
    width: "100%",
    padding: "10px",
    marginBottom: "15px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    backgroundColor: "#25273d",
    color: "#fff",
  },
  error: {
    color: "red",
    marginBottom: "15px",
  },
  link: {
    color: "#f39c12",
    textDecoration: "none",
    cursor: "pointer",
    transition: "all 0.3s ease-in-out",
  },
  linkHover: {
    color: "#fff",
    textDecoration: "underline",
    cursor: "pointer",
    transition: "all 0.3s ease-in-out",
  },
};

// Component Code
const AddMarks = () => {
  const [buttonHover, setButtonHover] = useState(false);
  const [linkHover, setLinkHover] = useState(false);

  return (
    <div style={styles.container}>
      <h2>Add Marks</h2>
      <form>
        <label style={styles.label}>Select Student</label>
        <select style={styles.select}>
          <option value="">-- Select a Student --</option>
          <option style={{ color: "#000" }}>Student 1</option>
          <option style={{ color: "#000" }}>Student 2</option>
        </select>

        <label style={styles.label}>Select Semester</label>
        <select style={styles.select}>
          <option value="">-- Select a Semester --</option>
          <option>Semester 1</option>
          <option>Semester 2</option>
        </select>

        <button
          style={buttonHover ? styles.buttonHover : styles.button}
          onMouseEnter={() => setButtonHover(true)}
          onMouseLeave={() => setButtonHover(false)}
        >
          Submit Marks
        </button>
      </form>

      {/* Add hover effect for links */}
      <p>
        <a
          style={linkHover ? styles.linkHover : styles.link}
          onMouseEnter={() => setLinkHover(true)}
          onMouseLeave={() => setLinkHover(false)}
          href="#"
        >
          Go Back to Dashboard
        </a>
      </p>
    </div>
  );
};

export default AddMarks;
