import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useHistory } from 'react-router-dom';

const AddMark = () => {
  const { rollNumber } = useParams(); // Get the student's roll number from the URL
  const history = useHistory();
  const [marks, setMarks] = useState({
    semester: '',
    subject1: '',
    subject2: '',
    subject3: '',
    subject4: '',
    subject5: '',
    subject6: ''
  });
  const [semesters, setSemesters] = useState([]);
  const [error, setError] = useState(null);

  // Fetch the list of semesters
  useEffect(() => {
    const fetchSemesters = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/semesters');
        setSemesters(response.data); // Set the semesters data
      } catch (err) {
        console.error('Error fetching semesters:', err);
        setError('Failed to fetch semesters. Please try again.');
      }
    };

    fetchSemesters();
  }, []);

  // Handle input change for marks and semester
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMarks((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handle form submission to save marks
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        `http://localhost:5000/api/addmarks/${rollNumber}`,
        marks
      );
      console.log(response.data); // Log success message
      alert('Marks added successfully');
      history.push('/students'); // Redirect back to the students list page after submission
    } catch (err) {
      console.error('Error adding marks:', err);
      alert('Failed to add marks. Please try again.');
    }
  };

  return (
    <div className="add-marks">
      <h2>Add Marks for Student: {rollNumber}</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>Semester:</label>
          <select
            name="semester"
            value={marks.semester}
            onChange={handleInputChange}
            required
          >
            <option value="">Select Semester</option>
            {semesters.map((semester) => (
              <option key={semester.id} value={semester.name}>
                {semester.name}
              </option>
            ))}
          </select>
        </div>

        {/* Subject 1 */}
        <div>
          <label>Subject 1 Name:</label>
          <input
            type="text"
            name="subject1"
            value={marks.subject1}
            onChange={handleInputChange}
            required
          />
          <label>Subject 1 Marks:</label>
          <input
            type="number"
            name="marks1"
            value={marks.marks1}
            onChange={handleInputChange}
            required
            min="0"
            max="100"
          />
        </div>

        {/* Subject 2 */}
        <div>
          <label>Subject 2 Name:</label>
          <input
            type="text"
            name="subject2"
            value={marks.subject2}
            onChange={handleInputChange}
            required
          />
          <label>Subject 2 Marks:</label>
          <input
            type="number"
            name="marks2"
            value={marks.marks2}
            onChange={handleInputChange}
            required
            min="0"
            max="100"
          />
        </div>

        {/* Subject 3 */}
        <div>
          <label>Subject 3 Name:</label>
          <input
            type="text"
            name="subject3"
            value={marks.subject3}
            onChange={handleInputChange}
            required
          />
          <label>Subject 3 Marks:</label>
          <input
            type="number"
            name="marks3"
            value={marks.marks3}
            onChange={handleInputChange}
            required
            min="0"
            max="100"
          />
        </div>

        {/* Subject 4 */}
        <div>
          <label>Subject 4 Name:</label>
          <input
            type="text"
            name="subject4"
            value={marks.subject4}
            onChange={handleInputChange}
            required
          />
          <label>Subject 4 Marks:</label>
          <input
            type="number"
            name="marks4"
            value={marks.marks4}
            onChange={handleInputChange}
            required
            min="0"
            max="100"
          />
        </div>

        {/* Subject 5 */}
        <div>
          <label>Subject 5 Name:</label>
          <input
            type="text"
            name="subject5"
            value={marks.subject5}
            onChange={handleInputChange}
            required
          />
          <label>Subject 5 Marks:</label>
          <input
            type="number"
            name="marks5"
            value={marks.marks5}
            onChange={handleInputChange}
            required
            min="0"
            max="100"
          />
        </div>

        {/* Subject 6 */}
        <div>
          <label>Subject 6 Name:</label>
          <input
            type="text"
            name="subject6"
            value={marks.subject6}
            onChange={handleInputChange}
            required
          />
          <label>Subject 6 Marks:</label>
          <input
            type="number"
            name="marks6"
            value={marks.marks6}
            onChange={handleInputChange}
            required
            min="0"
            max="100"
          />
        </div>

        <button type="submit">Save Marks</button>
      </form>
    </div>
  );
};

export default AddMark;
