import React, { useState, useEffect } from 'react';

const AddMarks = () => {
  const [students, setStudents] = useState([]);
  const [semesters] = useState(['Semester 1', 'Semester 2', 'Semester 3', 'Semester 4']); // Static list of semesters
  const [subjects] = useState({
    'Semester 1': ['Mathematics', 'Physics', 'Chemistry'],
    'Semester 2': ['Biology', 'Computer Science', 'History'],
    'Semester 3': ['Economics', 'English Literature', 'Sociology'],
    'Semester 4': ['Geography', 'Political Science', 'Statistics'],
  }); // Static mapping of semesters to subjects
  const [selectedStudent, setSelectedStudent] = useState('');
  const [selectedSemester, setSelectedSemester] = useState('');
  const [marks, setMarks] = useState({});
  const [error, setError] = useState('');

  // Fetch students from backend
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/students');
        const data = await response.json();
        setStudents(data); // Set the students in state
      } catch (err) {
        setError('Failed to fetch students.');
      }
    };
    fetchStudents();
  }, []);

  const handleStudentChange = (e) => {
    setSelectedStudent(e.target.value); // Update selected student
  };

  const handleSemesterChange = (e) => {
    setSelectedSemester(e.target.value); // Update selected semester
    setMarks({}); // Reset marks when semester changes
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMarks((prevMarks) => ({ ...prevMarks, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedStudent || !selectedSemester) {
      setError('Please select both a student and a semester.');
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/api/marks/${selectedStudent}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(marks),
      });
      const data = await response.json();
      alert(data.message); // Show success message
      setMarks({}); // Reset marks after submission
      setSelectedStudent(''); // Reset selected student
      setSelectedSemester(''); // Reset selected semester
    } catch (err) {
      setError('Failed to add marks. Please try again.');
    }
  };

  return (
    <div>
      <h2>Enter Marks for Student</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <form onSubmit={handleSubmit}>
        <label>Select Student:</label>
        <select
          value={selectedStudent}
          onChange={handleStudentChange}
          required
        >
          <option value="">--Select a Student--</option>
          {students.map((student) => (
            <option key={student.id} value={student.id}>
              {student.firstName} {student.lastName} (Roll No: {student.rollNumber})
            </option>
          ))}
        </select>

        <label>Select Semester:</label>
        <select
          value={selectedSemester}
          onChange={handleSemesterChange}
          required
        >
          <option value="">--Select a Semester--</option>
          {semesters.map((semester, index) => (
            <option key={index} value={semester}>
              {semester}
            </option>
          ))}
        </select>

        {selectedSemester &&
          subjects[selectedSemester]?.map((subject, index) => (
            <div key={index}>
              <label>{subject}</label>
              <input
                type="number"
                name={`marks${index + 1}`}
                value={marks[`marks${index + 1}`] || ''}
                onChange={handleInputChange}
                placeholder={`Marks for ${subject}`}
                required
              />
            </div>
          ))}

        <button type="submit">Save Marks</button>
      </form>
    </div>
  );
};

export default AddMarks;
